import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";import{o as a,c as p,e as t}from"./app.9b6c14fa.js";const r={},i=t('<h1 id="mq-\u600E\u4E48\u786E\u4FDD\u6D88\u606F100-\u4E0D\u4E22\u5931" tabindex="-1"><a class="header-anchor" href="#mq-\u600E\u4E48\u786E\u4FDD\u6D88\u606F100-\u4E0D\u4E22\u5931" aria-hidden="true">#</a> MQ\uFF1A\u600E\u4E48\u786E\u4FDD\u6D88\u606F100%\u4E0D\u4E22\u5931\uFF1F</h1><p>\u9762\u8BD5\u5B98\u5728\u9762\u8BD5\u5019\u9009\u4EBA\u65F6\uFF0C\u5982\u679C\u53D1\u73B0\u5019\u9009\u4EBA\u7684\u7B80\u5386\u4E2D\u5199\u4E86\u5728\u9879\u76EE\u4E2D\u4F7F\u7528\u4E86 MQ \u6280\u672F\uFF08\u5982 Kafka\u3001RabbitMQ\u3001RocketMQ\uFF09\uFF0C\u57FA\u672C\u90FD\u4F1A\u629B\u51FA\u4E00\u4E2A\u95EE\u9898\uFF1A\u5728\u4F7F\u7528 MQ \u7684\u65F6\u5019\uFF0C\u600E\u4E48\u786E\u4FDD\u6D88\u606F 100% \u4E0D\u4E22\u5931\uFF1F</p><p>\u8FD9\u4E2A\u95EE\u9898\u5728\u5B9E\u9645\u5DE5\u4F5C\u4E2D\u5F88\u5E38\u89C1\uFF0C\u65E2\u80FD\u8003\u5BDF\u5019\u9009\u8005\u5BF9\u4E8E MQ \u4E2D\u95F4\u4EF6\u6280\u672F\u7684\u638C\u63E1\u7A0B\u5EA6\uFF0C\u53C8\u80FD\u5F88\u597D\u5730\u533A\u5206\u5019\u9009\u4EBA\u7684\u80FD\u529B\u6C34\u5E73\u3002\u63A5\u4E0B\u6765\uFF0C\u6211\u4EEC\u5C31\u4ECE\u8FD9\u4E2A\u95EE\u9898\u51FA\u53D1\uFF0C\u63A2\u8BA8\u4F60\u5E94\u8BE5\u638C\u63E1\u7684\u57FA\u7840\u77E5\u8BC6\u548C\u7B54\u9898\u601D\u8DEF\uFF0C\u4EE5\u53CA\u5EF6\u4F38\u7684\u9762\u8BD5\u8003\u70B9\u3002</p><h3 id="\u6848\u4F8B\u80CC\u666F" tabindex="-1"><a class="header-anchor" href="#\u6848\u4F8B\u80CC\u666F" aria-hidden="true">#</a> \u6848\u4F8B\u80CC\u666F</h3><p>\u4EE5\u4EAC\u4E1C\u7CFB\u7EDF\u4E3A\u4F8B\uFF0C\u7528\u6237\u5728\u8D2D\u4E70\u5546\u54C1\u65F6\uFF0C\u901A\u5E38\u4F1A\u9009\u62E9\u7528\u4EAC\u8C46\u62B5\u6263\u4E00\u90E8\u5206\u7684\u91D1\u989D\uFF0C\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\uFF0C\u4EA4\u6613\u670D\u52A1\u548C\u4EAC\u8C46\u670D\u52A1\u901A\u8FC7 MQ \u6D88\u606F\u961F\u5217\u8FDB\u884C\u901A\u4FE1\u3002\u5728\u4E0B\u5355\u65F6\uFF0C\u4EA4\u6613\u670D\u52A1\u53D1\u9001\u201C\u6263\u51CF\u8D26\u6237 X 100 \u4E2A\u4EAC\u8C46\u201D\u7684\u6D88\u606F\u7ED9 MQ \u6D88\u606F\u961F\u5217\uFF0C\u800C\u4EAC\u8C46\u670D\u52A1\u5219\u5728\u6D88\u8D39\u7AEF\u6D88\u8D39\u8FD9\u6761\u547D\u4EE4\uFF0C\u5B9E\u73B0\u771F\u6B63\u7684\u6263\u51CF\u64CD\u4F5C\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/mq/100-budiushi-a5cbe077-5f38-44c6-9ed7-496fe1702cca.png" alt="" loading="lazy"></p><p>\u90A3\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\u4F60\u4F1A\u9047\u5230\u4EC0\u4E48\u95EE\u9898\u5462\uFF1F</p><h3 id="\u6848\u4F8B\u5206\u6790" tabindex="-1"><a class="header-anchor" href="#\u6848\u4F8B\u5206\u6790" aria-hidden="true">#</a> \u6848\u4F8B\u5206\u6790</h3><p>\u8981\u77E5\u9053\uFF0C\u5728\u4E92\u8054\u7F51\u9762\u8BD5\u4E2D\uFF0C\u5F15\u5165 MQ \u6D88\u606F\u4E2D\u95F4\u4EF6\u6700\u76F4\u63A5\u7684\u76EE\u7684\u662F\uFF1A\u505A\u7CFB\u7EDF\u89E3\u8026\u5408\u6D41\u91CF\u63A7\u5236\uFF0C\u8FFD\u5176\u6839\u6E90\u8FD8\u662F\u4E3A\u4E86\u89E3\u51B3\u4E92\u8054\u7F51\u7CFB\u7EDF\u7684\u9AD8\u53EF\u7528\u548C\u9AD8\u6027\u80FD\u95EE\u9898\u3002</p><ul><li>\u7CFB\u7EDF\u89E3\u8026\uFF1A \u7528 MQ \u6D88\u606F\u961F\u5217\uFF0C\u53EF\u4EE5\u9694\u79BB\u7CFB\u7EDF\u4E0A\u4E0B\u6E38\u73AF\u5883\u53D8\u5316\u5E26\u6765\u7684\u4E0D\u7A33\u5B9A\u56E0\u7D20\uFF0C\u6BD4\u5982\u4EAC\u8C46\u670D\u52A1\u7684\u7CFB\u7EDF\u9700\u6C42\u65E0\u8BBA\u5982\u4F55\u53D8\u5316\uFF0C\u4EA4\u6613\u670D\u52A1\u4E0D\u7528\u505A\u4EFB\u4F55\u6539\u53D8\uFF0C\u5373\u4F7F\u5F53\u4EAC\u8C46\u670D\u52A1\u51FA\u73B0\u6545\u969C\uFF0C\u4E3B\u4EA4\u6613\u6D41\u7A0B\u4E5F\u53EF\u4EE5\u5C06\u4EAC\u8C46\u670D\u52A1\u964D\u7EA7\uFF0C\u5B9E\u73B0\u4EA4\u6613\u670D\u52A1\u548C\u4EAC\u8C46\u670D\u52A1\u7684\u89E3\u8026\uFF0C\u505A\u5230\u4E86\u7CFB\u7EDF\u7684\u9AD8\u53EF\u7528\u3002</li><li>\u6D41\u91CF\u63A7\u5236\uFF1A \u9047\u5230\u79D2\u6740\u7B49\u6D41\u91CF\u7A81\u589E\u7684\u573A\u666F\uFF0C\u901A\u8FC7 MQ \u8FD8\u53EF\u4EE5\u5B9E\u73B0\u6D41\u91CF\u7684\u201C\u524A\u5CF0\u586B\u8C37\u201D\u7684\u4F5C\u7528\uFF0C\u53EF\u4EE5\u6839\u636E\u4E0B\u6E38\u7684\u5904\u7406\u80FD\u529B\u81EA\u52A8\u8C03\u8282\u6D41\u91CF\u3002 \u4E0D\u8FC7\u5F15\u5165 MQ \u867D\u7136\u5B9E\u73B0\u4E86\u7CFB\u7EDF\u89E3\u8026\u5408\u6D41\u91CF\u63A7\u5236\uFF0C\u4E5F\u4F1A\u5E26\u6765\u5176\u4ED6\u95EE\u9898\u3002</li></ul><p><strong>\u5F15\u5165 MQ \u6D88\u606F\u4E2D\u95F4\u4EF6\u5B9E\u73B0\u7CFB\u7EDF\u89E3\u8026\uFF0C\u4F1A\u5F71\u54CD\u7CFB\u7EDF\u4E4B\u95F4\u6570\u636E\u4F20\u8F93\u7684\u4E00\u81F4\u6027</strong>\u3002 \u5728\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\uFF0C\u5982\u679C\u4E24\u4E2A\u8282\u70B9\u4E4B\u95F4\u5B58\u5728\u6570\u636E\u540C\u6B65\uFF0C\u5C31\u4F1A\u5E26\u6765\u6570\u636E\u4E00\u81F4\u6027\u7684\u95EE\u9898\u3002\u540C\u7406\uFF0C\u5728\u8FD9\u4E00\u8BB2\u4F60\u8981\u89E3\u51B3\u7684\u5C31\u662F\uFF1A\u6D88\u606F\u751F\u4EA7\u7AEF\u548C\u6D88\u606F\u6D88\u8D39\u7AEF\u7684\u6D88\u606F\u6570\u636E\u4E00\u81F4\u6027\u95EE\u9898\uFF08\u4E5F\u5C31\u662F\u5982\u4F55\u786E\u4FDD\u6D88\u606F\u4E0D\u4E22\u5931\uFF09\u3002</p><p>\u800C\u5F15\u5165 MQ \u6D88\u606F\u4E2D\u95F4\u4EF6\u89E3\u51B3\u6D41\u91CF\u63A7\u5236\uFF0C \u4F1A\u4F7F\u6D88\u8D39\u7AEF\u5904\u7406\u80FD\u529B\u4E0D\u8DB3\u4ECE\u800C\u5BFC\u81F4\u6D88\u606F\u79EF\u538B\uFF0C\u8FD9\u4E5F\u662F\u4F60\u8981\u89E3\u51B3\u7684\u95EE\u9898\u3002</p><p>\u6240\u4EE5\u4F60\u80FD\u53D1\u73B0\uFF0C\u95EE\u9898\u4E0E\u95EE\u9898\u4E4B\u95F4\u5F80\u5F80\u662F\u73AF\u73AF\u76F8\u6263\u7684\uFF0C\u9762\u8BD5\u5B98\u4F1A\u501F\u673A\u8003\u5BDF\u4F60\u89E3\u51B3\u95EE\u9898\u601D\u8DEF\u7684\u8FDE\u8D2F\u6027\u548C\u77E5\u8BC6\u4F53\u7CFB\u7684\u638C\u63E1\u7A0B\u5EA6\u3002</p><p>\u90A3\u9762\u5BF9\u201C\u5728\u4F7F\u7528 MQ \u6D88\u606F\u961F\u5217\u65F6\uFF0C\u5982\u4F55\u786E\u4FDD\u6D88\u606F\u4E0D\u4E22\u5931\u201D\u8FD9\u4E2A\u95EE\u9898\u65F6\uFF0C\u4F60\u8981\u600E\u4E48\u56DE\u7B54\u5462\uFF1F\u9996\u5148\uFF0C\u4F60\u8981\u5206\u6790\u5176\u4E2D\u6709\u51E0\u4E2A\u8003\u70B9\uFF0C\u6BD4\u5982\uFF1A</p><ul><li>\u5982\u4F55\u77E5\u9053\u6709\u6D88\u606F\u4E22\u5931\uFF1F</li><li>\u54EA\u4E9B\u73AF\u8282\u53EF\u80FD\u4E22\u6D88\u606F\uFF1F</li><li>\u5982\u4F55\u786E\u4FDD\u6D88\u606F\u4E0D\u4E22\u5931\uFF1F</li></ul><p><strong>\u5019\u9009\u4EBA\u5728\u56DE\u7B54\u65F6\uFF0C\u8981\u5148\u8BA9\u9762\u8BD5\u5B98\u77E5\u9053\u4F60\u7684\u5206\u6790\u601D\u8DEF\uFF0C\u7136\u540E\u518D\u63D0\u4F9B\u89E3\u51B3\u65B9\u6848</strong>\uFF1A\u7F51\u7EDC\u4E2D\u7684\u6570\u636E\u4F20\u8F93\u4E0D\u53EF\u9760\uFF0C\u60F3\u8981\u89E3\u51B3\u5982\u4F55\u4E0D\u4E22\u6D88\u606F\u7684\u95EE\u9898\uFF0C\u9996\u5148\u8981\u77E5\u9053\u54EA\u4E9B\u73AF\u8282\u53EF\u80FD\u4E22\u6D88\u606F\uFF0C\u4EE5\u53CA\u6211\u4EEC\u5982\u4F55\u77E5\u9053\u6D88\u606F\u662F\u5426\u4E22\u5931\u4E86\uFF0C\u6700\u540E\u624D\u662F\u89E3\u51B3\u65B9\u6848\uFF08\u800C\u4E0D\u662F\u4E0A\u6765\u5C31\u76F4\u63A5\u8BF4\u81EA\u5DF1\u7684\u89E3\u51B3\u65B9\u6848\uFF09\u3002\u5C31\u597D\u6BD4\u201C\u67B6\u6784\u8BBE\u8BA1\u201D\u201C\u67B6\u6784\u201D\u4F53\u73B0\u4E86\u67B6\u6784\u5E08\u7684\u601D\u8003\u8FC7\u7A0B\uFF0C\u800C\u201C\u8BBE\u8BA1\u201D\u624D\u662F\u6700\u540E\u7684\u89E3\u51B3\u65B9\u6848\uFF0C\u4E24\u8005\u7F3A\u4E00\u4E0D\u53EF\u3002</p><h3 id="\u6848\u4F8B\u89E3\u7B54" tabindex="-1"><a class="header-anchor" href="#\u6848\u4F8B\u89E3\u7B54" aria-hidden="true">#</a> \u6848\u4F8B\u89E3\u7B54</h3><p>\u6211\u4EEC\u9996\u5148\u6765\u770B\u6D88\u606F\u4E22\u5931\u7684\u73AF\u8282\uFF0C\u4E00\u6761\u6D88\u606F\u4ECE\u751F\u4EA7\u5230\u6D88\u8D39\u5B8C\u6210\u8FD9\u4E2A\u8FC7\u7A0B\uFF0C\u53EF\u4EE5\u5212\u5206\u4E09\u4E2A\u9636\u6BB5\uFF0C\u5206\u522B\u4E3A\u6D88\u606F\u751F\u4EA7\u9636\u6BB5\uFF0C\u6D88\u606F\u5B58\u50A8\u9636\u6BB5\u548C\u6D88\u606F\u6D88\u8D39\u9636\u6BB5\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/mq/100-budiushi-1d1962fb-cb0f-41d0-8d6d-077ba33b6125.png" alt="" loading="lazy"></p><p><strong>\u6D88\u606F\u751F\u4EA7\u9636\u6BB5</strong>\uFF1A \u4ECE\u6D88\u606F\u88AB\u751F\u4EA7\u51FA\u6765\uFF0C\u7136\u540E\u63D0\u4EA4\u7ED9 MQ \u7684\u8FC7\u7A0B\u4E2D\uFF0C\u53EA\u8981\u80FD\u6B63\u5E38\u6536\u5230 MQ Broker \u7684 ack \u786E\u8BA4\u54CD\u5E94\uFF0C\u5C31\u8868\u793A\u53D1\u9001\u6210\u529F\uFF0C\u6240\u4EE5\u53EA\u8981\u5904\u7406\u597D\u8FD4\u56DE\u503C\u548C\u5F02\u5E38\uFF0C\u8FD9\u4E2A\u9636\u6BB5\u662F\u4E0D\u4F1A\u51FA\u73B0\u6D88\u606F\u4E22\u5931\u7684\u3002</p><p><strong>\u6D88\u606F\u5B58\u50A8\u9636\u6BB5</strong>\uFF1A \u8FD9\u4E2A\u9636\u6BB5\u4E00\u822C\u4F1A\u76F4\u63A5\u4EA4\u7ED9 MQ \u6D88\u606F\u4E2D\u95F4\u4EF6\u6765\u4FDD\u8BC1\uFF0C\u4F46\u662F\u4F60\u8981\u4E86\u89E3\u5B83\u7684\u539F\u7406\uFF0C\u6BD4\u5982 Broker \u4F1A\u505A\u526F\u672C\uFF0C\u4FDD\u8BC1\u4E00\u6761\u6D88\u606F\u81F3\u5C11\u540C\u6B65\u4E24\u4E2A\u8282\u70B9\u518D\u8FD4\u56DE ack\u3002</p><p><strong>\u6D88\u606F\u6D88\u8D39\u9636\u6BB5</strong>\uFF1A \u6D88\u8D39\u7AEF\u4ECE Broker \u4E0A\u62C9\u53D6\u6D88\u606F\uFF0C\u53EA\u8981\u6D88\u8D39\u7AEF\u5728\u6536\u5230\u6D88\u606F\u540E\uFF0C\u4E0D\u7ACB\u5373\u53D1\u9001\u6D88\u8D39\u786E\u8BA4\u7ED9 Broker\uFF0C\u800C\u662F\u7B49\u5230\u6267\u884C\u5B8C\u4E1A\u52A1\u903B\u8F91\u540E\uFF0C\u518D\u53D1\u9001\u6D88\u8D39\u786E\u8BA4\uFF0C\u4E5F\u80FD\u4FDD\u8BC1\u6D88\u606F\u7684\u4E0D\u4E22\u5931\u3002</p><p>\u65B9\u6848\u770B\u4F3C\u4E07\u65E0\u4E00\u5931\uFF0C\u6BCF\u4E2A\u9636\u6BB5\u90FD\u80FD\u4FDD\u8BC1\u6D88\u606F\u7684\u4E0D\u4E22\u5931\uFF0C\u4F46\u5728\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\uFF0C\u6545\u969C\u4E0D\u53EF\u907F\u514D\uFF0C\u4F5C\u4E3A\u6D88\u606F\u751F\u4EA7\u7AEF\uFF0C\u4F60\u5E76\u4E0D\u80FD\u4FDD\u8BC1 MQ \u662F\u4E0D\u662F\u5F04\u4E22\u4E86\u4F60\u7684\u6D88\u606F\uFF0C\u6D88\u8D39\u8005\u662F\u5426\u6D88\u8D39\u4E86\u4F60\u7684\u6D88\u606F\uFF0C\u6240\u4EE5\uFF0C\u672C\u7740 <code>Design for Failure</code> \u7684\u8BBE\u8BA1\u539F\u5219\uFF0C\u4F60\u8FD8\u662F\u9700\u8981\u4E00\u79CD\u673A\u5236\uFF0C\u6765 Check \u6D88\u606F\u662F\u5426\u4E22\u5931\u4E86\u3002</p><p><strong>\u7D27\u63A5\u7740\uFF0C\u4F60\u8FD8\u53EF\u4EE5\u5411\u9762\u8BD5\u5B98\u9610\u8FF0\u600E\u4E48\u8FDB\u884C\u6D88\u606F\u68C0\u6D4B</strong>\uFF1F \u603B\u4F53\u65B9\u6848\u89E3\u51B3\u601D\u8DEF\u4E3A\uFF1A\u5728\u6D88\u606F\u751F\u4EA7\u7AEF\uFF0C\u7ED9\u6BCF\u4E2A\u53D1\u51FA\u7684\u6D88\u606F\u90FD\u6307\u5B9A\u4E00\u4E2A\u5168\u5C40\u552F\u4E00 ID\uFF0C\u6216\u8005\u9644\u52A0\u4E00\u4E2A\u8FDE\u7EED\u9012\u589E\u7684\u7248\u672C\u53F7\uFF0C\u7136\u540E\u5728\u6D88\u8D39\u7AEF\u505A\u5BF9\u5E94\u7684\u7248\u672C\u6821\u9A8C\u3002</p><p>\u5177\u4F53\u600E\u4E48\u843D\u5730\u5B9E\u73B0\u5462\uFF1F\u4F60\u53EF\u4EE5\u5229\u7528\u62E6\u622A\u5668\u673A\u5236\u3002 \u5728\u751F\u4EA7\u7AEF\u53D1\u9001\u6D88\u606F\u4E4B\u524D\uFF0C\u901A\u8FC7\u62E6\u622A\u5668\u5C06\u6D88\u606F\u7248\u672C\u53F7\u6CE8\u5165\u6D88\u606F\u4E2D\uFF08\u7248\u672C\u53F7\u53EF\u4EE5\u91C7\u7528\u8FDE\u7EED\u9012\u589E\u7684 ID \u751F\u6210\uFF0C\u4E5F\u53EF\u4EE5\u901A\u8FC7\u5206\u5E03\u5F0F\u5168\u5C40\u552F\u4E00 ID\u751F\u6210\uFF09\u3002\u7136\u540E\u5728\u6D88\u8D39\u7AEF\u6536\u5230\u6D88\u606F\u540E\uFF0C\u518D\u901A\u8FC7\u62E6\u622A\u5668\u68C0\u6D4B\u7248\u672C\u53F7\u7684\u8FDE\u7EED\u6027\u6216\u6D88\u8D39\u72B6\u6001\uFF0C\u8FD9\u6837\u5B9E\u73B0\u7684\u597D\u5904\u662F\u6D88\u606F\u68C0\u6D4B\u7684\u4EE3\u7801\u4E0D\u4F1A\u4FB5\u5165\u5230\u4E1A\u52A1\u4EE3\u7801\u4E2D\uFF0C\u53EF\u4EE5\u901A\u8FC7\u5355\u72EC\u7684\u4EFB\u52A1\u6765\u5B9A\u4F4D\u4E22\u5931\u7684\u6D88\u606F\uFF0C\u505A\u8FDB\u4E00\u6B65\u7684\u6392\u67E5\u3002</p><p>\u8FD9\u91CC\u9700\u8981\u4F60\u6CE8\u610F\uFF1A\u5982\u679C\u540C\u65F6\u5B58\u5728\u591A\u4E2A\u6D88\u606F\u751F\u4EA7\u7AEF\u548C\u6D88\u606F\u6D88\u8D39\u7AEF\uFF0C\u901A\u8FC7\u7248\u672C\u53F7\u9012\u589E\u7684\u65B9\u5F0F\u5C31\u5F88\u96BE\u5B9E\u73B0\u4E86\uFF0C\u56E0\u4E3A\u4E0D\u80FD\u4FDD\u8BC1\u7248\u672C\u53F7\u7684\u552F\u4E00\u6027\uFF0C\u6B64\u65F6\u53EA\u80FD\u901A\u8FC7\u5168\u5C40\u552F\u4E00 ID \u7684\u65B9\u6848\u6765\u8FDB\u884C\u6D88\u606F\u68C0\u6D4B\uFF0C\u5177\u4F53\u7684\u5B9E\u73B0\u539F\u7406\u548C\u7248\u672C\u53F7\u9012\u589E\u7684\u65B9\u5F0F\u4E00\u81F4\u3002</p><p>\u73B0\u5728\uFF0C\u4F60\u5DF2\u7ECF\u77E5\u9053\u4E86\u54EA\u4E9B\u73AF\u8282\uFF08\u6D88\u606F\u5B58\u50A8\u9636\u6BB5\u3001\u6D88\u606F\u6D88\u8D39\u9636\u6BB5\uFF09\u53EF\u80FD\u4F1A\u51FA\u95EE\u9898\uFF0C\u5E76\u6709\u4E86\u5982\u4F55\u68C0\u6D4B\u6D88\u606F\u4E22\u5931\u7684\u65B9\u6848\uFF0C\u7136\u540E\u5C31\u8981\u7ED9\u51FA\u89E3\u51B3\u9632\u6B62\u6D88\u606F\u4E22\u5931\u7684\u8BBE\u8BA1\u65B9\u6848\u3002</p><p><strong>\u56DE\u7B54\u5B8C\u201C\u5982\u4F55\u786E\u4FDD\u6D88\u606F\u4E0D\u4F1A\u4E22\u5931\uFF1F\u201D \u4E4B\u540E\uFF0C\u9762\u8BD5\u5B98\u901A\u5E38\u4F1A\u8FFD\u95EE\u201C\u600E\u4E48\u89E3\u51B3\u6D88\u606F\u88AB\u91CD\u590D\u6D88\u8D39\u7684\u95EE\u9898\uFF1F</strong>\u201D</p><p>\u6BD4\u5982\uFF1A\u5728\u6D88\u606F\u6D88\u8D39\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u5982\u679C\u51FA\u73B0\u5931\u8D25\u7684\u60C5\u51B5\uFF0C\u901A\u8FC7\u8865\u507F\u7684\u673A\u5236\u53D1\u9001\u65B9\u4F1A\u6267\u884C\u91CD\u8BD5\uFF0C\u91CD\u8BD5\u7684\u8FC7\u7A0B\u5C31\u6709\u53EF\u80FD\u4EA7\u751F\u91CD\u590D\u7684\u6D88\u606F\uFF0C\u90A3\u4E48\u5982\u4F55\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\uFF1F</p><p>\u8FD9\u4E2A\u95EE\u9898\u5176\u5B9E\u53EF\u4EE5\u6362\u4E00\u79CD\u8BF4\u6CD5\uFF0C\u5C31\u662F\u5982\u4F55\u89E3\u51B3\u6D88\u8D39\u7AEF\u5E42\u7B49\u6027\u95EE\u9898\uFF08\u5E42\u7B49\u6027\uFF0C\u5C31\u662F\u4E00\u6761\u547D\u4EE4\uFF0C\u4EFB\u610F\u591A\u6B21\u6267\u884C\u6240\u4EA7\u751F\u7684\u5F71\u54CD\u5747\u4E0E\u4E00\u6B21\u6267\u884C\u7684\u5F71\u54CD\u76F8\u540C\uFF09\uFF0C\u53EA\u8981\u6D88\u8D39\u7AEF\u5177\u5907\u4E86\u5E42\u7B49\u6027\uFF0C\u90A3\u4E48\u91CD\u590D\u6D88\u8D39\u6D88\u606F\u7684\u95EE\u9898\u4E5F\u5C31\u89E3\u51B3\u4E86\u3002</p><p>\u6211\u4EEC\u8FD8\u662F\u6765\u770B\u6263\u51CF\u4EAC\u8C46\u7684\u4F8B\u5B50\uFF0C\u5C06\u8D26\u6237 X \u7684\u91D1\u8C46\u4E2A\u6570\u6263\u51CF 100 \u4E2A\uFF0C\u5728\u8FD9\u4E2A\u4F8B\u5B50\u4E2D\uFF0C\u6211\u4EEC\u53EF\u4EE5\u901A\u8FC7\u6539\u9020\u4E1A\u52A1\u903B\u8F91\uFF0C\u8BA9\u5B83\u5177\u5907\u5E42\u7B49\u6027\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/mq/100-budiushi-9d864624-2136-4770-942b-9a5f70c2aaf6.png" alt="" loading="lazy"></p><p>\u6700\u7B80\u5355\u7684\u5B9E\u73B0\u65B9\u6848\uFF0C\u5C31\u662F\u5728\u6570\u636E\u5E93\u4E2D\u5EFA\u4E00\u5F20\u6D88\u606F\u65E5\u5FD7\u8868\uFF0C \u8FD9\u4E2A\u8868\u6709\u4E24\u4E2A\u5B57\u6BB5\uFF1A\u6D88\u606F ID \u548C\u6D88\u606F\u6267\u884C\u72B6\u6001\u3002\u8FD9\u6837\uFF0C\u6211\u4EEC\u6D88\u8D39\u6D88\u606F\u7684\u903B\u8F91\u53EF\u4EE5\u53D8\u4E3A\uFF1A\u5728\u6D88\u606F\u65E5\u5FD7\u8868\u4E2D\u589E\u52A0\u4E00\u6761\u6D88\u606F\u8BB0\u5F55\uFF0C\u7136\u540E\u518D\u6839\u636E\u6D88\u606F\u8BB0\u5F55\uFF0C\u5F02\u6B65\u64CD\u4F5C\u66F4\u65B0\u7528\u6237\u4EAC\u8C46\u4F59\u989D\u3002</p><p>\u56E0\u4E3A\u6211\u4EEC\u6BCF\u6B21\u90FD\u4F1A\u5728\u63D2\u5165\u4E4B\u524D\u68C0\u67E5\u662F\u5426\u6D88\u606F\u5DF2\u5B58\u5728\uFF0C\u6240\u4EE5\u5C31\u4E0D\u4F1A\u51FA\u73B0\u4E00\u6761\u6D88\u606F\u88AB\u6267\u884C\u591A\u6B21\u7684\u60C5\u51B5\uFF0C\u8FD9\u6837\u5C31\u5B9E\u73B0\u4E86\u4E00\u4E2A\u5E42\u7B49\u7684\u64CD\u4F5C\u3002\u5F53\u7136\uFF0C\u57FA\u4E8E\u8FD9\u4E2A\u601D\u8DEF\uFF0C\u4E0D\u4EC5\u53EF\u4EE5\u4F7F\u7528\u5173\u7CFB\u578B\u6570\u636E\u5E93\uFF0C\u4E5F\u53EF\u4EE5\u901A\u8FC7 Redis \u6765\u4EE3\u66FF\u6570\u636E\u5E93\u5B9E\u73B0\u552F\u4E00\u7EA6\u675F\u7684\u65B9\u6848\u3002</p><p>\u5728\u8FD9\u91CC\u6211\u591A\u8BF4\u4E00\u53E5\uFF0C\u60F3\u8981\u89E3\u51B3\u201C\u6D88\u606F\u4E22\u5931\u201D\u548C\u201C\u6D88\u606F\u91CD\u590D\u6D88\u8D39\u201D\u7684\u95EE\u9898\uFF0C\u6709\u4E00\u4E2A\u524D\u63D0\u6761\u4EF6\u5C31\u662F\u8981\u5B9E\u73B0\u4E00\u4E2A\u5168\u5C40\u552F\u4E00 ID \u751F\u6210\u7684\u6280\u672F\u65B9\u6848\u3002\u8FD9\u4E5F\u662F\u9762\u8BD5\u5B98\u559C\u6B22\u8003\u5BDF\u7684\u95EE\u9898\uFF0C\u4F60\u4E5F\u8981\u638C\u63E1\u3002</p><p>\u5728\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\uFF0C\u5168\u5C40\u552F\u4E00 ID \u751F\u6210\u7684\u5B9E\u73B0\u65B9\u6CD5\u6709\u6570\u636E\u5E93\u81EA\u589E\u4E3B\u952E\u3001UUID\u3001Redis\uFF0CTwitter-Snowflake \u7B97\u6CD5\uFF0C\u6211\u603B\u7ED3\u4E86\u51E0\u79CD\u65B9\u6848\u7684\u7279\u70B9\uFF0C\u4F60\u53EF\u4EE5\u53C2\u8003\u4E0B\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/mq/100-budiushi-0622a500-83ef-4b8f-96d3-68c649d17311.png" alt="" loading="lazy"></p><p>\u6211\u63D0\u9192\u4F60\u6CE8\u610F\uFF0C\u65E0\u8BBA\u54EA\u79CD\u65B9\u6CD5\uFF0C\u5982\u679C\u4F60\u60F3\u540C\u65F6\u6EE1\u8DB3\u7B80\u5355\u3001\u9AD8\u53EF\u7528\u548C\u9AD8\u6027\u80FD\uFF0C\u5C31\u8981\u6709\u53D6\u820D\uFF0C\u6240\u4EE5\u4F60\u8981\u7AD9\u5728\u5B9E\u9645\u7684\u4E1A\u52A1\u4E2D\uFF0C\u8BF4\u660E\u4F60\u7684\u9009\u578B\u6240\u8003\u8651\u7684\u5E73\u8861\u70B9\u662F\u4EC0\u4E48\u3002\u6211\u4E2A\u4EBA\u5728\u4E1A\u52A1\u4E2D\u6BD4\u8F83\u503E\u5411\u4E8E\u9009\u62E9 Snowflake \u7B97\u6CD5\uFF0C\u5728\u9879\u76EE\u4E2D\u4E5F\u8FDB\u884C\u4E86\u4E00\u5B9A\u7684\u6539\u9020\uFF0C\u4E3B\u8981\u662F\u8BA9\u7B97\u6CD5\u4E2D\u7684 ID \u751F\u6210\u89C4\u5219\u66F4\u52A0\u7B26\u5408\u4E1A\u52A1\u7279\u70B9\uFF0C\u4EE5\u53CA\u4F18\u5316\u8BF8\u5982\u65F6\u949F\u56DE\u62E8\u7B49\u95EE\u9898\u3002</p><p>\u5F53\u7136\uFF0C\u9664\u4E86\u201C\u600E\u4E48\u89E3\u51B3\u6D88\u606F\u88AB\u91CD\u590D\u6D88\u8D39\u7684\u95EE\u9898\uFF1F\u201D\u4E4B\u5916\uFF0C\u9762\u8BD5\u5B98\u8FD8\u4F1A\u95EE\u5230\u4F60\u201C\u6D88\u606F\u79EF\u538B\u201D\u3002 \u539F\u56E0\u5728\u4E8E\u6D88\u606F\u79EF\u538B\u53CD\u6620\u7684\u662F\u6027\u80FD\u95EE\u9898\uFF0C\u89E3\u51B3\u6D88\u606F\u79EF\u538B\u95EE\u9898\uFF0C\u53EF\u4EE5\u8BF4\u660E\u5019\u9009\u8005\u6709\u80FD\u529B\u5904\u7406\u9AD8\u5E76\u53D1\u573A\u666F\u4E0B\u7684\u6D88\u8D39\u80FD\u529B\u95EE\u9898\u3002</p><p>\u4F60\u5728\u89E3\u7B54\u8FD9\u4E2A\u95EE\u9898\u65F6\uFF0C\u4F9D\u65E7\u8981\u4F20\u9012\u7ED9\u9762\u8BD5\u5B98\u4E00\u4E2A\u8FD9\u6837\u7684\u601D\u8003\u8FC7\u7A0B\uFF1A \u5982\u679C\u51FA\u73B0\u79EF\u538B\uFF0C\u90A3\u4E00\u5B9A\u662F\u6027\u80FD\u95EE\u9898\uFF0C\u60F3\u8981\u89E3\u51B3\u6D88\u606F\u4ECE\u751F\u4EA7\u5230\u6D88\u8D39\u4E0A\u7684\u6027\u80FD\u95EE\u9898\uFF0C\u5C31\u9996\u5148\u8981\u77E5\u9053\u54EA\u4E9B\u73AF\u8282\u53EF\u80FD\u51FA\u73B0\u6D88\u606F\u79EF\u538B\uFF0C\u7136\u540E\u5728\u8003\u8651\u5982\u4F55\u89E3\u51B3\u3002</p><p>\u56E0\u4E3A\u6D88\u606F\u53D1\u9001\u4E4B\u540E\u624D\u4F1A\u51FA\u73B0\u79EF\u538B\u7684\u95EE\u9898\uFF0C\u6240\u4EE5\u548C\u6D88\u606F\u751F\u4EA7\u7AEF\u6CA1\u6709\u5173\u7CFB\uFF0C\u53C8\u56E0\u4E3A\u7EDD\u5927\u90E8\u5206\u7684\u6D88\u606F\u961F\u5217\u5355\u8282\u70B9\u90FD\u80FD\u8FBE\u5230\u6BCF\u79D2\u949F\u51E0\u4E07\u7684\u5904\u7406\u80FD\u529B\uFF0C\u76F8\u5BF9\u4E8E\u4E1A\u52A1\u903B\u8F91\u6765\u8BF4\uFF0C\u6027\u80FD\u4E0D\u4F1A\u51FA\u73B0\u5728\u4E2D\u95F4\u4EF6\u7684\u6D88\u606F\u5B58\u50A8\u4E0A\u9762\u3002\u6BEB\u65E0\u7591\u95EE\uFF0C\u51FA\u95EE\u9898\u7684\u80AF\u5B9A\u662F\u6D88\u606F\u6D88\u8D39\u9636\u6BB5\uFF0C\u90A3\u4E48\u4ECE\u6D88\u8D39\u7AEF\u5165\u624B\uFF0C\u5982\u4F55\u56DE\u7B54\u5462\uFF1F</p><p>\u5982\u679C\u662F\u7EBF\u4E0A\u7A81\u53D1\u95EE\u9898\uFF0C\u8981\u4E34\u65F6\u6269\u5BB9\uFF0C\u589E\u52A0\u6D88\u8D39\u7AEF\u7684\u6570\u91CF\uFF0C\u4E0E\u6B64\u540C\u65F6\uFF0C\u964D\u7EA7\u4E00\u4E9B\u975E\u6838\u5FC3\u7684\u4E1A\u52A1\u3002\u901A\u8FC7\u6269\u5BB9\u548C\u964D\u7EA7\u627F\u62C5\u6D41\u91CF\uFF0C\u8FD9\u662F\u4E3A\u4E86\u8868\u660E\u4F60\u5BF9\u5E94\u6025\u95EE\u9898\u7684\u5904\u7406\u80FD\u529B\u3002</p><p>\u5176\u6B21\uFF0C\u624D\u662F\u6392\u67E5\u89E3\u51B3\u5F02\u5E38\u95EE\u9898\uFF0C\u5982\u901A\u8FC7\u76D1\u63A7\uFF0C\u65E5\u5FD7\u7B49\u624B\u6BB5\u5206\u6790\u662F\u5426\u6D88\u8D39\u7AEF\u7684\u4E1A\u52A1\u903B\u8F91\u4EE3\u7801\u51FA\u73B0\u4E86\u95EE\u9898\uFF0C\u4F18\u5316\u6D88\u8D39\u7AEF\u7684\u4E1A\u52A1\u5904\u7406\u903B\u8F91\u3002</p><p>\u6700\u540E\uFF0C\u5982\u679C\u662F\u6D88\u8D39\u7AEF\u7684\u5904\u7406\u80FD\u529B\u4E0D\u8DB3\uFF0C\u53EF\u4EE5\u901A\u8FC7\u6C34\u5E73\u6269\u5BB9\u6765\u63D0\u4F9B\u6D88\u8D39\u7AEF\u7684\u5E76\u53D1\u5904\u7406\u80FD\u529B\uFF0C\u4F46\u8FD9\u91CC\u6709\u4E00\u4E2A\u8003\u70B9\u9700\u8981\u7279\u522B\u6CE8\u610F\uFF0C \u90A3\u5C31\u662F\u5728\u6269\u5BB9\u6D88\u8D39\u8005\u7684\u5B9E\u4F8B\u6570\u7684\u540C\u65F6\uFF0C\u5FC5\u987B\u540C\u6B65\u6269\u5BB9\u4E3B\u9898 Topic \u7684\u5206\u533A\u6570\u91CF\uFF0C\u786E\u4FDD\u6D88\u8D39\u8005\u7684\u5B9E\u4F8B\u6570\u548C\u5206\u533A\u6570\u76F8\u7B49\u3002\u5982\u679C\u6D88\u8D39\u8005\u7684\u5B9E\u4F8B\u6570\u8D85\u8FC7\u4E86\u5206\u533A\u6570\uFF0C\u7531\u4E8E\u5206\u533A\u662F\u5355\u7EBF\u7A0B\u6D88\u8D39\uFF0C\u6240\u4EE5\u8FD9\u6837\u7684\u6269\u5BB9\u5C31\u6CA1\u6709\u6548\u679C\u3002</p><p>\u6BD4\u5982\u5728 Kafka \u4E2D\uFF0C\u4E00\u4E2A Topic \u53EF\u4EE5\u914D\u7F6E\u591A\u4E2A Partition\uFF08\u5206\u533A\uFF09\uFF0C\u6570\u636E\u4F1A\u88AB\u5199\u5165\u5230\u591A\u4E2A\u5206\u533A\u4E2D\uFF0C\u4F46\u5728\u6D88\u8D39\u7684\u65F6\u5019\uFF0CKafka \u7EA6\u5B9A\u4E00\u4E2A\u5206\u533A\u53EA\u80FD\u88AB\u4E00\u4E2A\u6D88\u8D39\u8005\u6D88\u8D39\uFF0CTopic \u7684\u5206\u533A\u6570\u91CF\u51B3\u5B9A\u4E86\u6D88\u8D39\u7684\u80FD\u529B\uFF0C\u6240\u4EE5\uFF0C\u53EF\u4EE5\u901A\u8FC7\u589E\u52A0\u5206\u533A\u6765\u63D0\u9AD8\u6D88\u8D39\u8005\u7684\u5904\u7406\u80FD\u529B\u3002</p><h3 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> \u603B\u7ED3</h3><p>\u81F3\u6B64\uFF0C\u6211\u4EEC\u8BB2\u89E3\u4E86 MQ \u6D88\u606F\u961F\u5217\u7684\u70ED\u95E8\u95EE\u9898\u7684\u89E3\u51B3\u65B9\u6848\uFF0C\u65E0\u8BBA\u662F\u521D\u4E2D\u7EA7\u8FD8\u662F\u9AD8\u7EA7\u7814\u53D1\u5DE5\u7A0B\u5E08\uFF0C\u672C\u7BC7\u6587\u7AE0\u7684\u5185\u5BB9\u90FD\u662F\u4F60\u9700\u8981\u638C\u63E1\u7684\uFF0C\u4F60\u90FD\u53EF\u4EE5\u4ECE\u8FD9\u51E0\u70B9\u51FA\u53D1\uFF0C\u4E0E\u9762\u8BD5\u5B98\u8FDB\u884C\u53CB\u597D\u7684\u4EA4\u6D41\u3002\u6211\u6765\u603B\u7ED3\u4E00\u4E0B\u4ECA\u5929\u7684\u91CD\u70B9\u5185\u5BB9\u3002</p><ul><li><p><strong>\u5982\u4F55\u786E\u4FDD\u6D88\u606F\u4E0D\u4F1A\u4E22\u5931</strong>\uFF1F \u4F60\u8981\u77E5\u9053\u4E00\u6761\u6D88\u606F\u4ECE\u53D1\u9001\u5230\u6D88\u8D39\u7684\u6BCF\u4E2A\u9636\u6BB5\uFF0C\u662F\u5426\u5B58\u5728\u4E22\u6D88\u606F\uFF0C\u4EE5\u53CA\u5982\u4F55\u76D1\u63A7\u6D88\u606F\u662F\u5426\u4E22\u5931\uFF0C\u6700\u540E\u624D\u662F\u5982\u4F55\u89E3\u51B3\u95EE\u9898\uFF0C\u65B9\u6848\u53EF\u4EE5\u57FA\u4E8E\u201C MQ \u7684\u53EF\u9760\u6D88\u606F\u6295\u9012 \u201D\u7684\u65B9\u5F0F\u3002</p></li><li><p><strong>\u5982\u4F55\u4FDD\u8BC1\u6D88\u606F\u4E0D\u88AB\u91CD\u590D\u6D88\u8D39</strong>\uFF1F \u5728\u8FDB\u884C\u6D88\u606F\u8865\u507F\u7684\u65F6\u5019\uFF0C\u4E00\u5B9A\u4F1A\u5B58\u5728\u91CD\u590D\u6D88\u606F\u7684\u60C5\u51B5\uFF0C\u90A3\u4E48\u5982\u4F55\u5B9E\u73B0\u6D88\u8D39\u7AEF\u7684\u5E42\u7B49\u6027\u5C31\u8FD9\u9053\u9898\u7684\u8003\u70B9\u3002</p></li><li><p><strong>\u5982\u4F55\u5904\u7406\u6D88\u606F\u79EF\u538B\u95EE\u9898</strong>\uFF1F \u8FD9\u9053\u9898\u7684\u8003\u70B9\u5C31\u662F\u5982\u4F55\u901A\u8FC7 MQ \u5B9E\u73B0\u771F\u6B63\u7684\u9AD8\u6027\u80FD\uFF0C\u56DE\u7B54\u7684\u601D\u8DEF\u662F\uFF0C\u672C\u7740\u89E3\u51B3\u7EBF\u4E0A\u5F02\u5E38\u4E3A\u6700\u9AD8\u4F18\u5148\u7EA7\uFF0C\u7136\u540E\u901A\u8FC7\u76D1\u63A7\u548C\u65E5\u5FD7\u8FDB\u884C\u6392\u67E5\u5E76\u4F18\u5316\u4E1A\u52A1\u903B\u8F91\uFF0C\u6700\u540E\u662F\u6269\u5BB9\u6D88\u8D39\u7AEF\u548C\u5206\u7247\u7684\u6570\u91CF\u3002</p></li></ul><p>\u5728\u56DE\u7B54\u95EE\u9898\u7684\u65F6\u5019\uFF0C\u4F60\u9700\u8981\u7279\u522B\u6CE8\u610F\u7684\u662F\uFF0C\u8BA9\u9762\u8BD5\u5B98\u4E86\u89E3\u5230\u4F60\u7684\u601D\u7EF4\u8FC7\u7A0B\uFF0C\u8FD9\u79CD\u89E3\u51B3\u95EE\u9898\u7684\u80FD\u529B\u662F\u9762\u8BD5\u5B98\u66F4\u4E3A\u770B\u4E2D\u7684\uFF0C\u6BD4\u4F60\u76F4\u63A5\u56DE\u7B54\u4E00\u9053\u9762\u8BD5\u9898\u66F4\u6709\u4EF7\u503C\u3002</p><p>\u53E6\u5916\uFF0C\u5982\u679C\u4F60\u5E94\u8058\u7684\u90E8\u95E8\u662F\u57FA\u7840\u67B6\u6784\u90E8\uFF0C\u90A3\u4E48\u9664\u4E86\u8981\u638C\u63E1\u672C\u8BB2\u4E2D\u7684\u5E38\u89C1\u95EE\u9898\u7684\u4E3B\u7EBF\u77E5\u8BC6\u4EE5\u5916\uFF0C\u8FD8\u8981\u638C\u63E1\u6D88\u606F\u4E2D\u95F4\u4EF6\u7684\u5176\u4ED6\u77E5\u8BC6\u4F53\u7CFB\uFF0C\u5982\uFF1A</p><ul><li>\u5982\u4F55\u9009\u578B\u6D88\u606F\u4E2D\u95F4\u4EF6\uFF1F</li><li>\u6D88\u606F\u4E2D\u95F4\u4EF6\u4E2D\u7684\u961F\u5217\u6A21\u578B\u4E0E\u53D1\u5E03\u8BA2\u9605\u6A21\u578B\u7684\u533A\u522B\uFF1F</li><li>\u4E3A\u4EC0\u4E48\u6D88\u606F\u961F\u5217\u80FD\u5B9E\u73B0\u9AD8\u541E\u5410\uFF1F</li><li>\u5E8F\u5217\u5316\u3001\u4F20\u8F93\u534F\u8BAE\uFF0C\u4EE5\u53CA\u5185\u5B58\u7BA1\u7406\u7B49\u95EE\u9898</li><li>\u2026 &gt;</li></ul><hr><blockquote><p>\u539F\u6587\u94FE\u63A5\uFF1Ahttp://t.hk.uy/Ue6</p></blockquote><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/xingbiaogongzhonghao.png" alt="" loading="lazy"></p>',54),o=[i];function n(l,s){return a(),p("div",null,o)}var h=e(r,[["render",n],["__file","100-budiushi.html.vue"]]);export{h as default};
