import{_ as p}from"./plugin-vue_export-helper.21dcd24c.js";import{o as e,c as t,e as o}from"./app.9b6c14fa.js";const r={},n=o('<h1 id="java\u7A7A\u6307\u9488nullpointerexception\u7684\u4F20\u8BF4" tabindex="-1"><a class="header-anchor" href="#java\u7A7A\u6307\u9488nullpointerexception\u7684\u4F20\u8BF4" aria-hidden="true">#</a> Java\u7A7A\u6307\u9488NullPointerException\u7684\u4F20\u8BF4</h1><p><strong>\u7A7A\u6307\u9488</strong>\uFF0C\u53F7\u79F0\u5929\u4E0B\u6700\u5F3A\u523A\u5BA2\u3002</p><p>\u4ED6\u539F\u672C\u4E0D\u53EB\u8FD9\u4E2A\u540D\u5B57\uFF0C\u7A7A\u6307\u9488\u539F\u672C\u590D\u59D3<strong>\u5F02\u5E38</strong>\uFF0C\u7A7A\u6307\u9488\u53EA\u4E0D\u8FC7\u662F\u4ED6\u7684\u6B66\u5668\uFF0C\u4F46\u4ED6\u6740\u622E\u8FC7\u591A\uFF0C\u6E10\u6E10\u5730\u4EBA\u4EEC\u53EA\u8BB0\u4F4F\u4E86\u7A7A\u6307\u9488\u8FD9\u4E09\u4E2A\u5B57\u3002</p><p>\u5929\u4E0B\u6B66\u529F\uFF0C\u552F\u5FEB\u4E0D\u7834\uFF0C\u7A7A\u6307\u9488\u7684\u9488\uFF0C\u4EE5\u5FEB\u548C\u8BE1\u5F02\u8457\u79F0\uFF0C\u7A0D\u6709\u4E0D\u614E\uFF0C\u4FBF\u662F\u4F24\u4EA1\u3002</p><p>... ...</p><p>\u6211\u53EB\u94C1\u67F1\uFF0C\u6211\u6765\u5230\u8FD9\u4E2A\u5947\u602A\u7684\u4E16\u754C\u5DF2\u7ECF\u4E00\u5E74\u4E86\uFF0C\u6211\u7B49\u4E86\u4E00\u5E74\uFF0C\u7A7F\u8D8A\u9644\u8D60\u7684\u8001\u7237\u7237\u3001\u6212\u6307\u3001\u7CFB\u7EDF\u4EC0\u4E48\u7684\u6211\u5230\u73B0\u5728\u90FD\u6CA1\u53D1\u73B0\u3002</p><p>\u800C\u4E14\u8FD9\u4E2A\u4E16\u754C\u770B\u8D77\u6765\u4E5F\u592A\u5947\u602A\u4E86\uFF0C\u8FD9\u91CC\u597D\u50CF\u53EB\u4EC0\u4E48\xA0<strong>Java</strong>\xA0\u5927\u9646\uFF0C\u6211\u53EA\u77E5\u9053\u8FD9\u4E2A\u4E16\u754C\u7684\u6700\u5F3A\u8005\u53EB\u505A\xA0<strong>Object</strong>\uFF0C\u542C\u8BF4\u662F\u4EC0\u4E48\u9053\u7956\u7EA7\u7684\u5B58\u5728\uFF0C\u6211\u4E5F\u4E0D\u77E5\u9053\u662F\u4EC0\u4E48\u610F\u601D\uFF0C\u6BD5\u7ADF\u6211\u73B0\u5728\u597D\u50CF\u8FD8\u662F\u4E2A\u83DC\u9E21\uFF0C\u522B\u7684\u4E3B\u89D2\u4E00\u5E74\u90FD\u5E94\u8BE5\u8981\u98DE\u5347\u4ED9\u754C\u4E86\u5427\uFF0C\u6211\u8FD8\u8FDE\u4E2A\u5C0F\u706B\u7403\u90FD\u653E\u4E0D\u51FA\u6765\u3002</p><p>\u54E6\uFF0C\u5BF9\u4E86\uFF0C\u4E0A\u9762\u7684\u90A3\u6BB5\u8BDD\u662F\u6211\u5728\u8336\u9986\u559D\u8336\u7684\u65F6\u5019\u542C\u8BF4\u4E66\u7684\u5148\u751F\u8BF4\u7684\uFF0C\u603B\u89C9\u5F97\u7A7A\u6307\u9488\u8FD9\u4E2A\u540D\u5B57\u602A\u602A\u7684\uFF0C\u597D\u50CF\u5728\u4EC0\u4E48\u5730\u65B9\u542C\u8BF4\u8FC7\u3002</p><p>\u6211\u7684\u5934\u75DB\u7684\u6BDB\u75C5\u53C8\u72AF\u4E86\uFF0C\u6211\u5DF2\u7ECF\u8BB0\u4E0D\u8D77\u6765\u6211\u4E3A\u4EC0\u4E48\u6765\u5230\u8FD9\u91CC\u4E86\uFF0C\u6211\u53EA\u8BB0\u5F97\u6211\u7684\u540D\u5B57\u53EB\u94C1\u67F1\uFF0C\u5176\u4ED6\u7684\uFF0C\u6211\u53EA\u611F\u89C9\u8FD9\u4E2A\u5947\u602A\u7684\u4E16\u754C\u6709\u4E00\u79CD\u719F\u6089\uFF0C\u4F46\u662F\u6211\u4EC0\u4E48\u90FD\u8BB0\u4E0D\u8D77\u6765\u4E86\u3002</p><p>\u7B97\u4E86\uFF0C\u5F97\u8FC7\u4E14\u8FC7\u5427\u3002</p><p>\u6211\u51C6\u5907\u53BB\u627E\u7A7A\u6307\u9488\u4E86\uFF0C\u867D\u7136\u542C\u8BF4\u4ED6\u5F88\u53EF\u6015\uFF0C\u4F46\u662F\u597D\u50CF\u542C\u8BF4\u4ED6\u4E0D\u662F\u55DC\u6740\u4E4B\u4EBA\uFF0C\u5E94\u8BE5\u4E0D\u4F1A\u6EE5\u6740\u65E0\u8F9C\u5427\uFF0C\u76EE\u524D\u4E3A\u6B62\uFF0C\u6211\u4E5F\u53EA\u5BF9\u8FD9\u4E09\u4E2A\u5B57\u6709\u719F\u6089\u7684\u611F\u89C9\u4E86\uFF0C\u6211\u4E00\u5B9A\u8981\u627E\u5230\u4ED6\uFF0C\u627E\u56DE\u6211\u7684\u8BB0\u5FC6\uFF01</p><p>\u6211\u6253\u542C\u4E86\u5F88\u4E45\uFF0C\u539F\u6765\u7A7A\u6307\u9488\u662F<strong>\u5F02\u5E38\u7EC4\u7EC7</strong>\u7684\u4E09\u4EE3\u5AE1\u4F20\uFF0C\u5F02\u5E38\u7EC4\u7EC7\u662F\u8FD9\u4E2A\u4E16\u754C\u4E0A\u6700\u6050\u6016\u7684\u6740\u624B\u7EC4\u7EC7\uFF0C\u7A7A\u6307\u9488\u5C31\u662F\u5F02\u5E38\u73B0\u5728\u6700\u51FA\u8272\u7684\u523A\u5BA2\u3002</p><p>\u542C\u8BF4\u7A7A\u6307\u9488\u51FA\u751F\u7684\u65F6\u5019\uFF0C\u8116\u5B50\u4E0A\u5C31\u6302\u7740\u4E00\u6839\u9488\uFF0C\u6574\u4E2A Java \u5927\u9646\u96EA\u4E0B\u4E00\u6708\u4E0D\u505C\uFF0CLinux \u68EE\u6797\u591A\u5757\u9646\u5730\u76F4\u63A5\u6C89\u9677\uFF0C\u4E8E\u662F\u4ED6\u7684\u7236\u4EB2\xA0<strong>RuntimeException</strong>\xA0\u5C31\u7ED9\u4ED6\u8D77\u4E86\u7A7A\u6307\u9488\u8FD9\u4E2A\u540D\u5B57\u3002</p><p>\u7A7A\u6307\u9488\u51FA\u751F\u7684\u5929\u751F\u5F02\u8C61\u4E5F\u5F15\u8D77\u4E86\u5F02\u5E38\u7EC4\u7EC7\u9AD8\u5C42\u7684\u6CE8\u610F\uFF0C\u542C\u8BF4\u4ED6\u7684\u7956\u7236\xA0<strong>Exception</strong>\uFF0C\u8FD8\u6709\u6574\u4E2A\u5F02\u5E38\u7EC4\u7EC7\u7684\u9886\u519B\u4EBA\u7269\xA0<strong>Throwable</strong>\xA0\u90FD\u4EB2\u81EA\u63A5\u89C1\u4E86\u7A7A\u6307\u9488\uFF0C\u5E76\u4E14\u8BA4\u4E3A\u7A7A\u6307\u9488\u5929\u8D4B\u5F02\u7980\uFF0C\u672A\u6765\u53EF\u671F\u3002</p><p>\u8981\u77E5\u9053\uFF0C<strong>Throwable</strong>\xA0\u53EF\u662F\xA0<strong>Object</strong>\xA0\u4EB2\u81EA\u4EFB\u547D\u7684\u5F02\u5E38\u7EC4\u7EC7\u5934\u9886\u3002\u4F5C\u4E3A Object \u6700\u503C\u5F97\u4FE1\u4EFB\u7684\u4EB2\u4FE1\uFF0C\u8DDF\u968F Object \u4E07\u5E74\u4EE5\u6765\uFF0C\u6240\u6709\u7684\u810F\u6D3B\u7D2F\u6D3B\u90FD\u4F9D\u9760 Thrwoable \u521B\u7ACB\u7684\u5F02\u5E38\u7EC4\u7EC7\u6765\u5904\u7406\uFF0C\u771F\u53EF\u8C13\u4E00\u4EBA\u4E4B\u4E0B\uFF0C\u4E07\u4EBA\u4E4B\u4E0A\u3002</p><p>Throwable \u53EA\u6709\u4E24\u4E2A\u4EB2\u5B50\uFF0C\u5C31\u662F Error \u548C Exception\uFF0C\u4F20\u8BF4\u4E2D Error \u5FC3\u72E0\u624B\u8FA3\uFF0C\u624B\u4E0B\u65E0\u4E00\u6D3B\u53E3\uFF0C\u89C1\u8FC7 Error \u7684\u4EBA\u8FD8\u80FD\u6D3B\u4E0B\u6765\u7684\u5BE5\u5BE5\u65E0\u51E0\u3002</p><p>\u6574\u4E2A\u5927\u9646\u53EA\u6709\u4ED6\u4EEC\u6050\u6016\u7684\u4F20\u8BF4\uFF0C\u8C01\u4E5F\u4E0D\u77E5\u9053\u4ED6\u4EEC\u4EC0\u4E48\u65F6\u5019\u51FA\u73B0\uFF0C\u4F46\u662F\u4E00\u65E6\u4ED6\u4EEC\u51FA\u73B0\uFF0C\u57FA\u672C\u5BA3\u544A\u7740\u4F60\u5DF2\u7ECF\u662F\u4E2A\u6B7B\u4EBA\u4E86\u3002</p><p>\u800C\u6211\u542C\u8BF4\u8FC7\u6700\u6050\u6016\u7684\u5C31\u662F<code>OutOfMemoryError</code>\xA0\u548C\xA0<code>StackOverflowError</code>\xA0\u8FD9\u4E24\u4F4D\u523A\u5BA2\uFF0C\u56E0\u4E3A\u5927\u9646\u4E0A\u6C38\u8FDC\u6709\u4E00\u5EA7\u98CE\u4E91\u699C\u60AC\u6302\u5728\u5E1D\u90FD\u95E8\u53E3\uFF0C\u800C\u8FD9\u4E24\u4F4D\uFF0C\u4E00\u76F4\u4F4D\u5C45\u6740\u624B\u699C\u699C\u9996\u4F4D\u7F6E\uFF0C\u7A7A\u6307\u9488\u4E5F\u53EA\u53EA\u80FD\u5C48\u5C45\u7B2C\u4E09\u800C\u5DF2\u3002\u5F53\u7136\uFF0C\u5927\u9646\u4E0D\u5C11\u4EBA\u90FD\u8BA4\u4E3A\u7A7A\u6307\u9488\u4F1A\u540E\u6765\u5C45\u4E0A\u3002</p><p>\u6211\u7684\u6D88\u606F\u53EA\u662F\u6253\u542C\u5230\u8FD9\u4E48\u591A\uFF0C\u63A5\u4E0B\u6765\u7684\u65E5\u5B50\uFF0C\u6211\u8D70\u8FC7\u65E0\u6570\u7684\u57CE\u5E02\u3001\u8352\u91CE\uFF0C\u6211\u7A7F\u8FC7\u6C99\u6F20\u3001\u4E1B\u6797\uFF0C\u8FD9\u4E00\u5929\uFF0C\u7EC8\u4E8E\uFF0C\u6211\u6765\u5230\u4E86\u5927\u9646\u7684\u5E1D\u90FD--<strong>\u5806</strong>\u3002</p><p>\u8FD9\u4E2A\u540D\u5B57\u542C\u8D77\u6765\u4E5F\u6709\u70B9\u8033\u719F\uFF0C\u4E0D\u7BA1\u4ED6\uFF0C\u5148\u8FDB\u57CE\u518D\u8BF4\u3002</p><p>\u8FDB\u57CE\u540E\u6211\u53D1\u73B0\u8FD9\u91CC\u975E\u5E38\u8BE1\u5F02\uFF0C\u6574\u5EA7\u57CE\u5E02\u597D\u50CF\u90FD\u975E\u5E38\u5E74\u8F7B\uFF0C\u597D\u50CF\u8FDE\u4E00\u4E2A\u6210\u5E74\u4EBA\u90FD\u6CA1\u6709\uFF01\u8857\u9053\u4E0A\u7199\u7199\u6518\u6518\u7ADF\u7136\u90FD\u662F\u5E74\u8F7B\u4EBA\u3002</p><p>\u5E26\u7740\u7591\u60D1\uFF0C\u6211\u8D70\u8FDB\u4E86\u4E00\u5BB6\u53EB\u505A<em>\u540C\u798F\u5BA2\u6808</em>\u7684\u9152\u697C\u3002</p><p>\u201D\u5BA2\u5B98\uFF0C\u6253\u5C16\u8FD8\u662F\u4F4F\u5E97\u554A\uFF1F\u201C\u4E00\u4E2A\u5C0F\u4E8C\u6A21\u6837\u7684<em>\u5C0F\u5B69</em>\u5E26\u7740\u4E00\u4E1D\u8C04\u5A9A\u7684\u5BF9\u6211\u8BF4\u3002</p><p>\u201D\u4F4F\u5E97\uFF0C\u5E26\u6211\u53BB\u6700\u597D\u7684\u623F\u95F4\uFF0C\u8FD9\u4E9B\u94B1\u5148\u62BC\u4F60\u8FD9\u91CC\uFF0C\u4E0D\u591F\u518D\u8DDF\u6211\u8981\u3002\u201C\u4E00\u8DEF\u8D70\u6765\uFF0C\u5BF9\u4E8E\u8FD9\u4E9B\u5730\u65B9\u7684\u884C\u60C5\u6211\u4E5F\u7B97\u8F7B\u8F66\u719F\u8DEF\u4E86\u3002</p><p>\u201D\u5C0F\u670B\u53CB\uFF0C\u8FD9\u91CC\u662F\u600E\u4E48\u56DE\u4E8B\uFF1F\u4F60\u4EEC\u8FD9\u91CC\u6CA1\u6709\u5927\u4EBA\u5417\uFF1F\u201C\u6211\u4E00\u8FB9\u8D70\u4E00\u8FB9\u95EE\u8FD9\u4E2A\u53EA\u6709\u6211\u4E00\u534A\u8EAB\u9AD8\u7684\u5C0F\u5B69\uFF0C\u6839\u636E\u6211\u76EE\u6D4B\uFF0C\u4ED6\u8EAB\u9AD8\u4E0D\u8D85\u8FC71\u7C73\uFF0C\u5E94\u8BE5\u8FD8\u53EA\u6709\u4E03\u516B\u5C81\u7684\u6837\u5B50\uFF0C\u96BE\u9053\u8FD9\u91CC\u7684\u5546\u4EBA\u5982\u6B64\u9ED1\u5FC3\uFF0C\u7ADF\u7136\u96C7\u4F63\u7AE5\u5DE5\uFF0C\u4E0D\u8FC7\u8FD9\u4E5F\u4E0D\u8C8C\u4F3C\u4E0D\u5BF9\uFF0C\u56E0\u4E3A\u5468\u56F4\u7684\u5BA2\u4EBA\u597D\u50CF\u4E5F\u90FD\u662F\u8FD9\u822C\u5E74\u7EAA\uFF0C\u4ED6\u4EEC\u7ADF\u7136\u8FD8\u6709\u5728\u62BD\u70DF\u559D\u9152\u7684\uFF01</p><p>\u201D\u5BA2\u5B98\u53EF\u771F\u5E7D\u9ED8\uFF0C\u4E0D\u8FC7\u6211\u770B\u5BA2\u5B98\u5E94\u8BE5\u662F\u521A\u6765\u5E1D\u90FD\uFF0C\u4E0D\u7792\u60A8\u8BF4\uFF0C\u6574\u4E2A\u5E1D\u90FD\u5C31\u57FA\u672C\u6CA1\u6709\u8D85\u8FC715\u5C81\u7684\u4EBA\uFF0C\u8D85\u8FC715\u7684\u636E\u8BF4\u90FD\u5728\u53EB\u505A\u8001\u5E74\u533A\u7684\u517B\u8001\u53BB\u4E86\uFF01\u5C31\u62FF\u6211\u6765\u8BF4\u5427\uFF0C\u6211\u4ECA\u5E74\u53EF\u4E0D\u5C0F\u4E86\uFF0C\u6211\u90FD8\u5C81\u4E86\uFF0C\u50CF\u6211\u8FD9\u822C\u5E74\u7EAA\u7684\u5DF2\u7ECF\u534A\u622A\u817F\u8FC8\u8FDB\u68FA\u6750\u54AF\u3002\u54CE\uFF0C\u8FD9\u8EAB\u5B50\u4E5F\u662F\u4E00\u5E74\u4E0D\u5982\u4E00\u5E74\u4E86\u3002\u201C</p><p>\u770B\u7740\u8FD9\u4E2A\u5C0F\u4E8C\u4E00\u8138\u8BA4\u771F\u7684\u6837\u5B50\uFF0C\u6211\u8D8A\u53D1\u89C9\u5F97\u8FD9\u5EA7\u57CE\u5E02\u8BE1\u5F02\u8D77\u6765\u4E86\uFF018\u5C81\uFF0C\u4EC0\u4E48\u9B3C\uFF1F8\u5C81\u4E0D\u662F\u5E94\u8BE5\u5728\u5BB6\u91CC\u770B\u559C\u7F8A\u7F8A\u5417\uFF1F\uFF01\u8FD8\u534A\u622A\u817F\u8FC8\u8FDB\u68FA\u6750\uFF01</p><p>\u201D\u53EF\u662F\u4F60\u770B\u6211\u6BD4\u4F60\u9AD8\u8FD9\u4E48\u591A\uFF0C\u4F60\u4E0D\u89C9\u5F97\u5947\u602A\u5417\uFF1F\u201C\u6211\u5947\u602A\u7684\u95EE\u4ED6\u3002</p><p>\u201D\u6709\u4EC0\u4E48\u597D\u5947\u602A\u7684\uFF0C\u8981\u4E0D\u662F\u6211\u5C0F\u65F6\u5019\u559D\u591A\u4E86\u4E09\u9E7F\uFF0C\u6CA1\u51C6\u6211\u4E5F\u957F\u8FD9\u4E48\u9AD8\u4E86\uFF01\u201C\u5C0F\u4E8C\u6709\u70B9\u751F\u6C14\u7684\u5BF9\u6211\u8BF4\u3002</p><p>\u884C\u5427\uFF0C\u518D\u8BF4\u4E24\u53E5\u628A\u4ED6\u6FC0\u6012\u4E86\uFF0C\u8DF3\u8D77\u6765\u6253\u6211\u819D\u76D6\u5C31\u5927\u4E8B\u4E0D\u5999\u4E86\u3002</p><p>\u63A5\u4E0B\u6765\u7684\u51E0\u5929\uFF0C\u7ECF\u8FC7\u6211\u7684\u6253\u63A2\uFF0C\u539F\u6765\u6211\u5728\u7684\u5730\u65B9\u662F\u53EB\u505A\u5E74\u8F7B\u533A\uFF0C\u6574\u4E2A\u5E1D\u90FD\u5C31\u53EA\u6709\u8FD9\u4E24\u4E2A\u533A\u57DF\uFF0C\u5E74\u8F7B\u533A\u7684\u4EBA\u5E74\u9F84\u786E\u5B9E\u6CA1\u6709\u8D85\u8FC715\u5C81\u7684\uFF0C\u6709\u4E9B\u4EBA\u521A\u51FA\u751F\u6CA1\u51E0\u5929\u5C31\u6B7B\u4E86\uFF0C\u5BF9\u6B64\uFF0C\u751F\u6D3B\u5728\u8FD9\u91CC\u7684\u4EBA\u4E5F\u89C1\u602A\u4E0D\u602A\u4E86\u3002\u5BF9\u4E8E\u4ED6\u4EEC\u6765\u8BF4\uFF0C\u5BC4\u5E0C\u671B\u80FD\u6D3B\u5230\u8D85\u8FC715\u5C81\u8FDB\u5165\u8001\u5E74\u533A\u517B\u8001\u5C31\u662F\u4ED6\u4EEC\u7684\u68A6\u60F3\u3002</p><p>\u6211\u5728\u6000\u7591\u662F\u4E0D\u662F\u5F02\u5E38\u7EC4\u7EC7\u5728\u8FD9\u91CC\u6697\u6740\uFF0C\u53EF\u662F\u53D1\u73B0\u7ED3\u679C\u5E76\u4E0D\u662F\uFF0C\u8FD9\u91CC\u7684\u4EBA\u8C8C\u4F3C\u5DF2\u7ECF\u4E60\u60EF\u4E86\uFF0C\u751F\u6D3B\u5BF9\u4ED6\u4EEC\u6765\u8BF4\u5C31\u662F\u968F\u4FBF\u6D3B\u6D3B\u5C31\u597D\u4E86\uFF0C\u6BCF\u6B21\u7684\u6B7B\u4EA1\u5BF9\u4E8E\u4ED6\u4EEC\u6765\u8BF4\u6BEB\u65E0\u5F81\u5146\uFF0C\u53EF\u80FD\u521A\u8E22\u7740\u7403\u5462\uFF0C\u5C31\u7A81\u7136\u6302\u4E86\uFF0C\u6709\u7684\u4E0A\u7740\u5395\u6240\u7A81\u7136\u5C31\u6B7B\u4E86\uFF0C\u4E34\u6B7B\u524D\u8FDE\u4E2A\u5C41\u80A1\u90FD\u6CA1\u64E6\uFF0C\u4E0D\u8BF4\u4E86\uFF0C\u6709\u70B9\u6076\u5FC3\u3002</p><p>\u5C31\u5728\u6211\u7B49\u7684\u4E0D\u8010\u70E6\u60F3\u6253\u7B97\u53BB\u8001\u5E74\u533A\u770B\u770B\u7684\u65F6\u5019\uFF0C\u4E00\u4E2A\u7A7F\u7740\u9ED1\u8863\u7684\u4EBA\u627E\u5230\u4E86\u6211\u3002</p><p>\u201D\u4F60\u662F\u8C01\uFF1F\u201C\u6211\u8B66\u60D5\u7684\u95EE\u4ED6\u3002</p><p>\u201D\u672C\u5EA7IOException\u3002\u201C\u9ED1\u8863\u4EBA\u795E\u60C5\u51B0\u51B7\u7684\u770B\u7740\u6211\u8BF4\u3002</p><p>\u201D\u4F60\u627E\u6211\u4EC0\u4E48\u4E8B\uFF1F\u201C</p><p>\u201D\u8FD9\u4E9B\u4F60\u4E0D\u7528\u77E5\u9053\uFF0C\u8DDF\u6211\u8D70\u4E00\u8D9F\u5427\uFF01\u201C</p><p>\u6211\u521A\u60F3\u8BF4\u8BDD\u62D2\u7EDD\uFF0C\u5F00\u4EC0\u4E48\u73A9\u7B11\uFF0C\u8DDF\u4F60\u4EEC\u5F02\u5E38\u7EC4\u6253\u4EA4\u9053\u7684\u4EBA\u975E\u6B7B\u5373\u6B8B\uFF0C\u8C01\u8981\u8DDF\u4F60\u53BB\u3002</p><p>\u4F46\u662F\u7531\u4E0D\u5F97\u6211\u62D2\u7EDD\uFF0C\u6211\u53EA\u611F\u89C9\u4E00\u9635\u5929\u65CB\u5730\u8F6C\uFF0C\u6211\u611F\u89C9\u6211\u5728\u5929\u4E0A\u98DE\uFF0C\u7136\u540E\u6211\u5C31\u5931\u53BB\u4E86\u610F\u8BC6\u3002\u5F53\u6211\u9192\u6765\u7684\u65F6\u5019\uFF0C\u6211\u53D1\u73B0\u6211\u8EBA\u5728\u4E00\u5F20\u5DE8\u5927\u7684\u5E8A\u4E0A\uFF0C\u684C\u5B50\u4E0A\u70B9\u7740\u4E00\u652F\u6A80\u9999\uFF0C\u6574\u4E2A\u623F\u95F4\u53EA\u6709\u4E00\u5F20\u684C\u5B50\u3001\u4E00\u628A\u6905\u5B50\u548C\u6211\u8EBA\u7684\u5730\u65B9\u3002</p><p>\u623F\u95F4\u5F88\u5C0F\uFF0C\u5E94\u8BE5\u53EA\u670910\u51E0\u4E2A\u5E73\u65B9\uFF0C\u4F46\u662F\u6211\u7ADF\u7136\u53C8\u6709\u4E00\u79CD\u719F\u6089\u7684\u611F\u89C9\uFF0C\u8FD9\u79CD\u611F\u89C9\u8426\u7ED5\u5728\u6211\u5FC3\u5934\u6325\u6563\u4E0D\u53BB\u3002</p><p>\u6CA1\u7B49\u6211\u518D\u60F3\u66F4\u591A\uFF0C\u623F\u95E8\u6253\u5F00\u4E86\u3002</p><p>\u201D\u662F\u4F60\uFF0C\u4F60\u628A\u6211\u5E26\u6765\u5E72\u4EC0\u4E48\uFF1F\u201C</p><p>\u201D\u8D70\u5427\uFF0C\u6709\u4EBA\u8981\u89C1\u4F60\u3002\u201C</p><p>\u8FD8\u662F\u4E0D\u5BB9\u6211\u6297\u62D2\uFF0C\u5982\u679C\u6211\u7684\u6218\u6597\u529B\u662F5\u7684\u8BDD\uFF0C\u6211\u60F3\uFF0CIO\u4ED6\u8BE5\u6709\u597D\u51E0\u4E07\u4E86\u5427\u3002</p><p>\u53C8\u662F\u8FD9\u8BE5\u6B7B\u7684\u7729\u6655\u611F\uFF0C\u4E0D\u8FC7\u8FD9\u6B21\u6CA1\u6709\u51E0\u79D2\u949F\uFF0C\u6211\u5C31\u53D1\u73B0\u6211\u5728\u4E00\u4E2A\u82B1\u56ED\u91CC\uFF0C\u82B1\u56ED\u4E2D\u95F4\u4E00\u4E2A\u8EAB\u7A7F\u9EC4\u888D\u7684\u4E2D\u5E74\u4EBA\u6B63\u5728\u6162\u60A0\u60A0\u7684\u559D\u8336\u3002\u5728\u4ED6\u8EAB\u4E0A\u6211\u611F\u53D7\u4E0D\u5230\u4EFB\u4F55\u5F3A\u5927\u7684\u6C14\u606F\uFF0C\u751A\u81F3\u4E0D\u5982IOException\u7ED9\u6211\u7684\u538B\u8FEB\u611F\u5F3A\u70C8\u3002\u8FD9\u662F\u8C01\uFF1F</p><p>\u4E0D\u7B49\u6211\u601D\u7EEA\u98D8\u98DE\uFF0CIOException\u5F2F\u8170\u8EAC\u8EAB\u8BF4\u9053\uFF1A\u201D\u965B\u4E0B\uFF0C\u4EBA\u5E26\u8FC7\u6765\u4E86\u3002\u201C</p><p>\u201D\u55EF\uFF0C\u4F60\u9000\u4E0B\u5427\u3002\u201C\u4E2D\u5E74\u4EBA\u8F6C\u8FC7\u8EAB\u6765\uFF0C\u8138\u4E0A\u4E1D\u6BEB\u770B\u4E0D\u51FA\u60C5\u7EEA\u7684\u8BF4\u9053\u3002</p><p>\u6211\u5927\u6982\u731C\u5230\u4E86\u8FD9\u662F\u54EA\u91CC\u4E86\uFF0C\u4E8E\u662F\u4E5F\u653E\u4E0B\u5FC3\u6765\uFF0C\u5728\u8FD9\u91CC\uFF0C\u6216\u8BB8\u80FD\u627E\u5230\u6211\u7684\u7B54\u6848\u3002</p><p>\u53CD\u6B63\u4ED6\u8981\u5BF9\u6211\u600E\u4E48\u6837\uFF0C\u6211\u4E5F\u6CA1\u6709\u529E\u6CD5\u53CD\u6297\uFF0C\u6211\u5F84\u76F4\u5750\u5230\u4ED6\u7684\u5BF9\u9762\uFF0C\u770B\u7740\u4ED6\u8BF4\uFF1A\u201D\u60A8\u5C31\u662FObject\u965B\u4E0B\u5427\uFF0C\u4E0D\u77E5\u627E\u6211\u6240\u8C13\u4F55\u4E8B\uFF1F\u201C</p><p>\u4E2D\u5E74\u4EBA\u4E5F\u4E0D\u5728\u610F\uFF0C\u6CA1\u6709\u6B63\u9762\u56DE\u7B54\u6211\u7684\u95EE\u9898\uFF0C\u53CD\u800C\u7565\u5E26\u4E00\u4E1D\u8C03\u4F83\u7684\u8BF4\u9053\uFF1A\u201D\u4E0D\u7528\u54AC\u6587\u56BC\u5B57\uFF0C\u8BF4\u70B9\u6B63\u5E38\u4EBA\u7684\u8BDD\u5427\u3002\u201C</p><p>... ...</p><p>\u8FD9\u4E0D\u6309\u5957\u8DEF\u51FA\u724C\u554A\uFF0C\u6211\u8FD9\u4E0D\u662F\u6765\u4E45\u4E86\uFF0C\u6A21\u4EFF\u4F60\u4EEC\u53E4\u4EE3\u4EBA\u8BF4\u8BDD\u561B\uFF0C\u600E\u4E48\u8FD8\u57CB\u6028\u8D77\u6211\u6765\u4E86\uFF1F\uFF01</p><p>\u201D\u90A3\u6211\u5C31\u76F4\u8BF4\u4E86\uFF0C\u6211\u60F3\u77E5\u9053\u7A7A\u6307\u9488\u5728\u54EA\u91CC\u3002\u201C</p><p>\u201D\u7A7A\u6307\u9488\u5C31\u5728\u7687\u5BAB\u8F6E\u503C\uFF0C\u4F60\u627E\u4ED6\u5E72\u561B\uFF1F\u201C</p><p>\u201D\u6211\u6682\u65F6\u4E0D\u80FD\u8BF4\u201C</p><p>\u201D\u5475\u5475\uFF0C\u4F60\u5C31\u4E0D\u597D\u5947\u6211\u4E3A\u4EC0\u4E48\u77E5\u9053\u4F60\uFF0C\u4E3A\u4EC0\u4E48\u53C8\u628A\u4F60\u5E26\u8FC7\u6765\uFF1F\u201C</p><p>\u201D\u597D\u5947\uFF0C\u53EF\u662F\u6211\u5C31\u662F\u4E0D\u60F3\u95EE\u3002\u201C</p><p>Object\u559D\u4E86\u53E3\u8336\uFF0C\u4E0D\u7D27\u4E0D\u6162\u7684\u56DE\u9053\uFF1A\u201D\u5E74\u8F7B\u4EBA\u6709\u6027\u683C\u662F\u597D\u4E8B\uFF0C\u53EF\u662F\u8FC7\u521A\u6613\u6298\u7684\u9053\u7406\u4F60\u5E94\u8BE5\u660E\u767D\u3002\u201C</p><p>\u201D\u6211\u4E0D\u660E\u767D\uFF0C\u6211\u5728\u8FD9\u91CC\u53CD\u6B63\u4E5F\u6CA1\u770B\u89C1\u4EC0\u4E48\u8001\u4EBA\uFF0C\u5F53\u7136\uFF0C\u9664\u4E86\u4F60\u3002\u201C\u6211\u7406\u6240\u5F53\u7136\u7684\u8BA4\u4E3A\u8FD9\u80AF\u5B9A\u662FObject\u641E\u5F97\u9B3C\uFF0C\u6574\u4E2A\u5E1D\u90FD\u90FD\u662F\u5C0F\u670B\u53CB\uFF0C\u8981\u662F\u6CA1\u6709\u732B\u817B\uFF0C\u9A97\u9B3C\u5462\uFF01</p><p>Object\u542C\u5230\u8FD9\u8BDD\uFF0C\u76B1\u4E86\u76B1\u7709\uFF0C\u4ED6\u6C89\u9ED8\u4E86\u4E00\u4F1A\u513F\uFF0C\u7F13\u7F13\u7AD9\u8D77\u8EAB\u5B50\u8D70\u5230\u4E00\u9897\u67F3\u6811\u4E0B\uFF0C\u80CC\u7740\u624B\u8BF4\u9053\uFF1A\u201C\u4F60\u4E0D\u77E5\u9053\u8FD9\u4E00\u5207\u662F\u4E3A\u4EC0\u4E48\u5417\uFF1F\u201D</p><p>\u5E9F\u8BDD\uFF0C\u6211\u5F53\u7136\u4E0D\u77E5\u9053\u4E86\uFF0C\u6211\u77E5\u9053\u8FD8\u80FD\u95EE\u4F60\u5417\uFF1F\uFF01</p><p>\u53C8\u662F\u6C89\u9ED8... ...\u8FD9\u4E2A\u6C14\u6C1B\u8BA9\u6211\u611F\u89C9\u5F88\u4E0D\u8212\u670D\u3002\u5C31\u5728\u6211\u53D7\u4E0D\u4E86\u60F3\u8BF4\u8BDD\u7684\u65F6\u5019\uFF0CObject\u7A81\u7136\u8BF4\u4E86\u4E00\u53E5\uFF1A\u201C\u5E26\u4ED6\u53BB\u89C1\u7A7A\u6307\u9488\u5427\u3002\u201D</p><p>\u201C\u662F\uFF0C\u965B\u4E0B\uFF01\u201D\u7A81\u7136\uFF0C\u4E00\u4E2A\u8EAB\u7A7F\u7EA2\u888D\u7684\u67AF\u7626\u8001\u8005\u51FA\u73B0\u5728\u6211\u80CC\u540E\uFF0C\u628A\u6211\u5413\u4E86\u4E00\u8DF3\u3002</p><p>\u6211\u4E5F\u4E0D\u60F3\u518D\u591A\u751F\u4E8B\u7AEF\uFF0C\u76F4\u89C9\u544A\u8BC9\u6211\u8FD9\u91CC\u4E0D\u662F\u4E45\u7559\u4E4B\u5730\uFF0C\u867D\u7136\u6709\u70B9\u83AB\u540D\u5176\u5999\uFF0C\u6211\u8FD8\u662F\u8DDF\u7740\u7EA2\u888D\u8001\u8005\u6765\u8D70\u4E86\u3002</p><p>... ...</p><p>\u201C\u965B\u4E0B\uFF0C\u662F\u4ED6\u5417\uFF1F\u201D\u4E00\u4E2A\u5149\u5934\u5927\u6C49\u7684\u8EAB\u5F71\u5728\u534A\u7A7A\u82E5\u5F71\u82E5\u73B0\u7684\u8BF4\u9053\u3002</p><p>\u201C\u8FD8\u4E0D\u80FD\u786E\u5B9A... \u4E0D\u8FC7\uFF0C\u7559\u7ED9\u6211\u4EEC\u7684\u65F6\u95F4\u4E0D\u591A\u4E86\uFF0C\u4E0B\u4E00\u6B21\u7684\u8F6E\u56DE\u5C31\u5FEB\u6765\u4E86\u3002\u201D</p><p>\u201C\u8F6E\u56DE\uFF0C\u53C8\u662F\u8F6E\u56DE\u3002\u6211\u4EEC\u8FD8\u6709\u5E0C\u671B\u5417\uFF1F\u201D\u5927\u6C49\u5462\u5583\u7740\uFF0C\u4E0D\u77E5\u9053\u662F\u5BF9\u81EA\u5DF1\u8BF4\u8FD8\u662F\u5BF9\u4E2D\u5E74\u4EBA\u8BF4\u3002</p><p>\u4E2D\u5E74\u4EBA\u4F9D\u7136\u80CC\u7740\u624B\uFF0C\u62AC\u5934\u671B\u7740\u6F2B\u5929\u7684\u67F3\u7D6E\u8BF4\u9053\uFF1A\u201C\u8FD9\u4E00\u4E16\uFF0C\u8BE5\u662F\u4E2A\u4E86\u65AD\u4E86\u3002\u201D</p><p>... ...</p><p>\u6CA1\u591A\u4E45\uFF0C\u4ED6\u628A\u6211\u5E26\u5230\u4E00\u4E2A\u623F\u95F4\u95E8\u53E3\uFF0C\u4E5F\u662F\u9762\u65E0\u8868\u60C5\u7684\u8BF4\u9053\uFF1A\u201C\u8FDB\u53BB\u5427\uFF0C\u7A7A\u6307\u9488\u5C31\u5728\u91CC\u9762\u3002\u201D</p><p>\u6211\u633A\u4F4F\u811A\u6B65\uFF0C\u8F6C\u8FC7\u8EAB\u95EE\u4ED6\uFF1A\u201C\u4F60\u662F\u8C01\uFF1F\u6211\u4EEC\u662F\u4E0D\u662F\u89C1\u8FC7\uFF1F\u201D</p><p>\u7EA2\u888D\u8001\u8005\u602A\u5F02\u4E00\u7B11\uFF1A\u201C\u4E5F\u8BB8\u5427\uFF0C\u8001\u592B<code>IndexOutOfBoundsException</code>\uFF0C\u7A7A\u6307\u9488\u4FBF\u662F\u6211\u597D\u53CB\u3002\u201D</p><p>\u8FD9\u4E2A\u540D\u5B57\u53EF\u771F\u957F\uFF0C\u6211\u542C\u8BF4\u8FC7\u4ED6\uFF0C\u636E\u4F20\u95FB\u4ED6\u7684\u5B9E\u529B\u4E5F\u975E\u5E38\u4E4B\u5F3A\uFF0C\u53EF\u80FD\u4E0D\u4E0B\u4E8E\u7A7A\u6307\u9488\uFF0C\u90FD\u662F\u4EE5\u8BE1\u5F02\u7684\u51FA\u624B\u89D2\u5EA6\u8457\u79F0\uFF0C\u4E0D\u8FC7\u76F8\u6BD4\u4E8E\u7A7A\u6307\u9488\u7684\u5927\u540D\uFF0C\u4ED6\u597D\u50CF\u66F4\u4F4E\u8C03\uFF0C\u96BE\u602A\u5728\u7687\u5BAB\u5F53\u4E2A\u8001\u592A\u76D1\u4E00\u822C\u3002</p><p>\u6211\u4E5F\u4E0D\u5728\u591A\u60F3\uFF0C\u70B9\u70B9\u5934\uFF0C\u8D70\u8FDB\u4E86\u623F\u95F4\u3002\u521A\u8FDB\u623F\u95F4\uFF0C\u6211\u5C31\u770B\u89C1\u4E00\u4E2A\u4E00\u8EAB\u767D\u8863\u7684\u8EAB\u5F71\u80CC\u5BF9\u7740\u6211\uFF0C\u7B14\u76F4\u7684\u8EAB\u5F71\u597D\u50CF\u8981\u51B2\u7834\u5929\u9645\uFF0C\u8EAB\u4E0A\u7684\u6C14\u52BF\u5F3A\u5927\u65E0\u6BD4\uFF0C\u81F3\u5C11\u5728\u6211\u89C1\u8FC7\u7684\u6240\u6709\u4EBA\u91CC\u8DB3\u4EE5\u6392\u8FDB\u524D\u4E09\u4E86\u3002\u7A7A\u6307\u9488\uFF0C\u679C\u7136\u540D\u4E0D\u865A\u4F20\uFF01</p><p>\u6211\u8D70\u5230\u623F\u95F4\u4E2D\u592E\uFF0C\u73AF\u76EE\u56DB\u671B\u624D\u53D1\u73B0\u8FD9\u597D\u50CF\u662F\u4E00\u5EA7\u7960\u5802\u7684\u6837\u5B50\uFF0C\u5C31\u5728\u6211\u8FD8\u5728\u6253\u91CF\u56DB\u5468\u4E4B\u9645\uFF0C\u4E00\u9053\u6E05\u51B7\u7684\u58F0\u97F3\u4F20\u5230\u6211\u7684\u8033\u8FB9\uFF1A</p><p>\u201C\u4F60\u8EAB\u4E0A\u7684\u6C14\u606F\u8BA9\u6211\u975E\u5E38\u8BA8\u538C\uFF01\u201D</p><p>\u4ED6\u8F6C\u8FC7\u8EAB\u6765\uFF0C\u6211\u53D1\u73B0\u6211\u6839\u672C\u770B\u4E0D\u6E05\u7A7A\u6307\u9488\u957F\u4EC0\u4E48\u6837\u5B50\uFF0C\u4ED6\u7684\u8138\u597D\u50CF\u6253\u4E0A\u4E86\u9A6C\u8D5B\u514B\u3002\u542C\u5230\u4ED6\u7684\u8BDD\uFF0C\u6211\u5FC3\u91CC\u7684\u7591\u60D1\u66F4\u591A\u4E86\uFF0C\u6211\u53EA\u662F\u89C9\u5F97\u4ED6\u7684\u6C14\u606F\u8BA9\u6211\u611F\u5230\u975E\u5E38\u719F\u6089\uFF0C\u4ED6\u7684\u8BDD\u8BA9\u6211\u6709\u70B9\u83AB\u540D\u5176\u5999\u3002\u4E8E\u662F\u6211\u8BD5\u63A2\u9053\uFF1A</p><p>\u201C\u4F60\u77E5\u9053\u6211\u662F\u8C01\uFF1F\u201D</p><p>\u542C\u5230\u6211\u7684\u8BDD\uFF0C\u4ED6\u4E00\u6B65\u6B65\u8D70\u8FDB\u6211\uFF0C\u5728\u6211\u8EAB\u8FB9\u95FB\u4E86\u95FB\uFF0C\u8FD9\u8BA9\u6211\u4EC0\u4E48\u4E00\u7D27\uFF0C\u867D\u7136\u6211\u60F3\u641E\u6E05\u695A\u6211\u8EAB\u4E0A\u7684\u95EE\u9898\uFF0C\u4F46\u662F\u6211\u4E0D\u662F\u51FA\u5356\u8089\u4F53\u7684\u4EBA\uFF0C\u6211\u9000\u540E\u4E00\u6B65\u8BF4\uFF1A</p><p>\u201C\u4F60\u60F3\u5E72\u561B\uFF1F\u201D</p><p>\u7A7A\u6307\u9488\u76B1\u7D27\u4E86\u7709\u5934\uFF0C\u4EFF\u4F5B\u81EA\u8A00\u81EA\u8BED\u9053\uFF1A\u201C\u4E0D\u5BF9\uFF0C\u4E0D\u5BF9\uFF0C\u8FD9\u662F... \u89C4\u5219\u7684\u6C14\u606F\uFF1F\u53EF\u662F\u4ED6\u660E\u660E\u8EAB\u4E0A\u6CA1\u6709\u4EFB\u4F55\u80FD\u91CF\u6CE2\u52A8\u3002\u201D</p><p>\u6211\u89C1\u4ED6\u597D\u50CF\u9B54\u6014\u4E86\uFF0C\u4EFF\u4F5B\u5728\u601D\u8003\u4EC0\u4E48\uFF0C\u4E8E\u662F\u8FC8\u6B65\u8D70\u5230\u4ED6\u521A\u624D\u7AD9\u7ACB\u7684\u5730\u65B9\u770B\u7740\u524D\u9762\uFF0C\u539F\u6765\uFF0C\u8FD9\u662F\u4ED6\u4EEC\u7684\u65CF\u8C31\uFF01\u8FD9\u91CC\u662F\u5F02\u5E38\u7684\u7960\u5802\uFF01</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/exception/npe-1.jpg" alt="" loading="lazy"></p><p>\u770B\u5B8C\u8FD9\u5F20\u65CF\u8C31\uFF0C\u6211\u604D\u7136\u5927\u609F\uFF0C\u597D\u50CF\u660E\u767D\u4E86\u4EC0\u4E48\u3002\u7A81\u7136\uFF0C\u6211\u7684\u8111\u888B\u91CC\u51FA\u73B0\u4E86\u4E00\u4E2A\u51B0\u51B7\u7684\u673A\u5668\u58F0\u97F3\uFF1A\u201C\u83B7\u53D6\u5F02\u5E38\u65CF\u8C31\uFF0C\u5386\u7EC3\u5B8C\u6210\u5EA6+100\u3002\u201D</p><p>\u6211Kao\uFF0C\u7CFB\u7EDF\uFF0C\u8FD9\u662F\u7CFB\u7EDF\u554A\uFF0C\u6211\u4E0D\u7981\u5185\u725B\u6EE1\u9762\uFF0C\u5565\u4EFB\u52A1\u7CFB\u7EDF\u554A\uFF0C\u4E00\u70B9\u63D0\u793A\u90FD\u6CA1\u6709\uFF0C\u6211\u8D76\u7D27\u558A\u9053\uFF1A</p><p>\u201C\u7CFB\u7EDF\uFF0C\u7CFB\u7EDF\uFF0C\u8FD8\u5728\u5417\uFF1F\u5728\u7EBF\u7B49\uFF0C\u633A\u7740\u6025\u7684\u3002\u201D\u53EF\u662F\u6CA1\u6709\u4EFB\u4F55\u56DE\u590D\uFF01\u8FD9\u5565\u7834\u7CFB\u7EDF\uFF01\u5C31\u5728\u6211\u60F3\u7834\u53E3\u5927\u9A82\u7684\u65F6\u5019\uFF0C\u7A7A\u6307\u9488\u770B\u5230\u6211\u548C\u4E2A\u4E8C\u50BB\u5B50\u4F3C\u7684\u5927\u547C\u5C0F\u53EB\uFF0C\u7A81\u7136\u4E00\u8138\u4E0D\u53EF\u601D\u8BAE\u7684\u5BF9\u7740\u6211\u8BF4\uFF1A</p><p>\u201C\u4F60\u660E\u609F\u4E86\u89C4\u5219\uFF1F\u201D</p><p>\u6211\u6123\u4E86\u6123\uFF0C\u55EF\uFF1F\u96BE\u9053\u6211\u4E0D\u662F\u62185\u6E23\u4E86\uFF1F\u89C4\u5219\u4E4B\u529B\uFF1F\u597D\u50CF\u662F\u5F88\u9AD8\u7AEF\u7684\u6837\u5B50\u554A\uFF1F</p><p>\u201C\u6492\u8C46\u6210\u5175\uFF01\u201D</p><p>\u201C\u547C\u98CE\uFF01\u201D</p><p>\u201D\u5524\u96E8\uFF01\u201C</p><p>\u201D\u4E34\u5175\u6597\u8005\u7686\u9635\u5217\u5728\u524D\uFF01\u201C</p><p>\u4E00\u70B9\u53CD\u5E94\u90FD\u6CA1\u6709\u3002\u3002\u3002\u5565\u73A9\u610F\u513F\uFF1F\u8FD8\u89C4\u5219\u4E4B\u529B\uFF1F\u4E5D\u5B57\u771F\u8A00\u90FD\u6CA1\u7528\u554A\uFF1F</p><p>\u7A7A\u6307\u9488\u597D\u50CF\u90FD\u8499\u4E86\uFF0C\u4ED6\u6572\u4E86\u6572\u592A\u9633\u7A74\uFF0C\u65E0\u8BED\u7684\u770B\u7740\u6211\u8BF4\uFF1A</p><p>\u201D\u4F60\u4E0D\u662F\u6765\u627E\u6211\u7684\u5417\uFF1F\u8BF4\u5B8C\u4F60\u7684\u95EE\u9898\uFF0C\u7136\u540E\u7ED9\u6211\u6EDA\uFF01\u201C</p><p>\u5BF9\u554A\uFF0C\u8FD9\u7CFB\u7EDF\u628A\u6211\u6574\u7684\u6211\u90FD\u5FD8\u8BB0\u6211\u6765\u5E72\u561B\u7684\u4E86\uFF0C\u6211\u8D76\u7D27\u8BF4\uFF1A</p><p>\u201D\u4F60\u8BA4\u8BC6\u6211\u5BF9\u4E0D\u5BF9\uFF0C\u4F60\u662F\u4E0D\u662F\u89C9\u5F97\u6211\u6709\u4E00\u79CD\u719F\u6089\u7684\u611F\u89C9\uFF1F\u6211\u60F3\u77E5\u9053\u6211\u7684\u6765\u5386\uFF01\u201C</p><p>\u7A7A\u6307\u9488\u53C8\u6123\u4E86\u6123\uFF0C\u4ED6\u770B\u7740\u6211\uFF0C\u6C89\u9ED8\u4E86\u4E00\u4F1A\u513F\uFF0C\u56DE\u9053\uFF1A\u201C\u4E0D\u77E5\u9053\uFF01\u201D</p><p>\u6211\u6709\u70B9\u5947\u602A\uFF0C\u770B\u4ED6\u4E00\u8138\u4FBF\u79D8\u7684\u8868\u60C5\u5E94\u8BE5\u662F\u89C1\u8FC7\u6211\u7684\uFF0C\u4ED6\u4E00\u5B9A\u5728\u6492\u8C0E\uFF0C\u65E2\u7136\u5982\u6B64...</p><p>\u201C\u90A3\u4F60\u544A\u8BC9\u6211\u4F60\u4EEC\u6709\u4EC0\u4E48\u529E\u6CD5\u80FD\u5728\u4F60\u4EEC\u5F02\u5E38\u7684\u653B\u51FB\u4E0B\u9632\u8EAB\u5427\uFF1F\u201D</p><p>\u7A7A\u6307\u9488\u5927\u6012\uFF0C\u521A\u60F3\u8D77\u8EAB\u8BF4\u8BDD\uFF0C\u7A7A\u4E2D\u7A81\u7136\u4F20\u6765\u4E00\u9053\u58F0\u97F3\uFF1A\u7B54\u5E94\u4ED6\u7684\u8981\u6C42\uFF01</p><p>\u4ED6\u51B7\u54FC\u4E00\u58F0\uFF0C\u4E22\u7ED9\u6211\u4E00\u672C\u4E66\uFF0C\u4E0A\u9762\u5199\u7740<strong>catch</strong>\u4E00\u4E2A\u5B57\uFF0C\u8FD8\u6709\u4E00\u5757\u5199\u7740<strong>catch</strong>\u7684\u4EE4\u724C\uFF0C\u51B0\u51B7\u7684\u8BF4\u5230\uFF1A\u201C\u4F60\u60F3\u77E5\u9053\u7684\u90FD\u5728\u8FD9\u91CC\u4E86\u3002\u201D\u8BF4\u5B8C\uFF0C\u62C2\u8896\u800C\u53BB\u3002</p><p>\u6211\u770B\u7740\u684C\u5B50\u4E0A\u7684\u8FD9\u672C\u4E66\uFF0C\u60F3\u4E86\u60F3\u8FD8\u662F\u7FFB\u9605\u8D77\u6765\u3002</p><p>\u539F\u6765<code>Exception</code>\xA0\u548C\u5B83\u7684\u513F\u5B50\u4EEC\uFF0C\u9664\u4E86<code>RuntimeException</code>\xA0\u4E00\u652F\uFF0C\u90FD\u53EB\u4F5C<code>Checked Exception</code>\uFF0C\u6211\u8FD8\u80FD\u7528catch\u4EE4\u724C\u6765\u5BF9\u6297\u4ED6\u4EEC\u7684\u653B\u51FB\uFF01\u5305\u62EC\u7A7A\u6307\u9488\uFF0C\u4EE5\u540E\u6211\u5C31\u4E0D\u6015\u4ED6\u4EEC\u4E86\uFF01</p><p>\u53EF\u662F\uFF0C\u4ED6\u4E3A\u4EC0\u4E48\u8981\u7ED9\u6211\uFF0C\u770B\u4ED6\u521A\u624D\u7684\u6837\u5B50\u90FD\u60F3\u6253\u6211\u4E86\uFF0C\u53C8\u7A81\u7136\u7ED9\u4E86\u6211\u8FD9\u4E9B\uFF1F\u8FD8\u6709\u4ED6\u4E00\u76F4\u5728\u8BF4\u7684\u89C4\u5219\u4E4B\u529B\u53C8\u662F\u4EC0\u4E48\uFF1F\u8FD9\u5EA7\u57CE\u5E02\u4E3A\u4EC0\u4E48\u53C8\u8FD9\u4E48\u8BE1\u5F02\uFF1F</p><blockquote><p>\u8F6C\u8F7D\u94FE\u63A5\uFF1Ahttps://mp.weixin.qq.com/s/PDfd8HRtDZafXl47BCxyGg</p></blockquote><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/xingbiaogongzhonghao.png" alt="" loading="lazy"></p>',108),c=[n];function a(s,i){return e(),t("div",null,c)}var l=p(r,[["render",a],["__file","npe.html.vue"]]);export{l as default};
