import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";import{r,o as l,c as n,b as e,a as t,d as a,e as p}from"./app.9b6c14fa.js";const c={},s=e("h1",{id:"\u6625\u62DB\u65A9\u83B7\u6DF1\u5733\u817E\u8BAFpcg\u548C\u676D\u5DDE\u963F\u91CC\u4E91",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#\u6625\u62DB\u65A9\u83B7\u6DF1\u5733\u817E\u8BAFpcg\u548C\u676D\u5DDE\u963F\u91CC\u4E91","aria-hidden":"true"},"#"),a(" \u6625\u62DB\u65A9\u83B7\u6DF1\u5733\u817E\u8BAFPCG\u548C\u676D\u5DDE\u963F\u91CC\u4E91")],-1),h=a("\u8F6C\u8F7D\u94FE\u63A5\uFF1A"),d={href:"https://mp.weixin.qq.com/s/EB_NCC2fNyYHuz04k1H4Nw",target:"_blank",rel:"noopener noreferrer"},u=a("https://mp.weixin.qq.com/s/EB_NCC2fNyYHuz04k1H4Nw"),b=p('<p>\u5927\u5BB6\u597D\uFF0C\u6211\u662F\u4E8C\u54E5\u5440\uFF01\u6625\u62DB\u5373\u5C06\u5230\u6765\uFF0C\u795D\u53C2\u52A0\u9762\u8BD5\u7684\u5C0F\u4F19\u4F34\u90FD\u80FD\u987A\u5229\u62FF\u5230\u6EE1\u610F\u7684 offer!</p><p>\u4E3A\u4E86\u7ED9\u5C0F\u4F19\u4F34\u4EEC\u4E00\u70B9\u70B9\u5E2E\u52A9\u548C\u542F\u53D1\uFF0C\u6211\u4E5F\u4F1A\u5206\u4EAB\u4E00\u4E9B\u6BD4\u8F83\u6709\u4EF7\u503C\u7684\u8BFB\u8005\u9762\u7ECF\uFF0C\u8FD9\u662F\u4E00\u7BC7\u6765\u81EA\u4E00\u4F4D\u8001\u8BFB\u8005\u7684\u786C\u6838\u6625\u62DB\u9762\u7ECF\u5206\u4EAB\u3002\u5185\u5BB9\u5305\u62EC\uFF1A</p><ul><li>\u57FA\u672C\u60C5\u51B5\uFF08\u5B66\u6821\u3001offer \u60C5\u51B5......\uFF09</li><li>\u4E3A\u4EC0\u4E48\u9009\u62E9\u4E92\u8054\u7F51\u884C\u4E1A\u7814\u53D1\u5C97</li><li>\u5B66\u4E60 Java \u5F00\u53D1\u7684\u7ECF\u9A8C\u603B\u7ED3</li><li>\u5B66\u4E60\u65B9\u6CD5\u548C\u5FC3\u6001\u4E0A\u7684\u5EFA\u8BAE</li><li>\u8BFB\u7814\u76F8\u5173</li></ul><hr><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/mianjing/shanganaliyun-1.png" alt="" loading="lazy"></p><p>\u6211\u7684\u6635\u79F0\u662F\u98CE\uFF0C\u6765\u81EA\u6D59\u6C5F\u821F\u5C71\uFF0C\u5357\u822A\u8BFB\u7814\uFF0C\u6B66\u5927\u672C\u79D1\uFF0C\u4E13\u4E1A\u662F\u8BA1\u7B97\u673A\u3002</p><p><em>\u4E8C\u54E5 : \u672C\u7855\u90FD\u662F\u540D\u6821\uFF01TQL\uFF01\u5149\u8FD9\u4E00\u70B9\u5C31\u9886\u5148\u4E86\u5927\u90E8\u5206\u6C42\u804C\u8005</em>\u3002</p><p>\u8003\u7814\u7ED3\u675F\u9009\u62E9\u4E86 Java \u5F00\u53D1\u5C97\u4F5C\u4E3A\u5DE5\u4F5C\uFF0C\u5728\u7814\u7A76\u751F\u9636\u6BB5\u9009\u62E9\u653E\u517B\u578B\u7684\u5BFC\u5E08\uFF0C\u56E0\u6B64\u6709\u5927\u91CF\u7A7A\u95F2\u65F6\u95F4 \u9762\u5411\u5C31\u4E1A\u5B66\u4E60 \uFF0C\u7CFB\u7EDF\u5730\u5B66\u4E60\u4E86\u5F88\u591A\u904D\u57FA\u7840\u77E5\u8BC6\uFF0C\u5237\u4E86\u5F88\u591A\u904D\u7B97\u6CD5\u9898\u3002\u8DDF\u7740\u6559\u7A0B\u505A\u4E86\u4E00\u4E2A\u8BBA\u575B\u7C7B\u7F51\u7AD9\u7684\u9879\u76EE\uFF0C\u5728\u53C2\u52A0\u7684\u5B66\u751F\u4FF1\u4E50\u90E8\u91CC\u63A5\u5230\u5B66\u6821\u8001\u5E08\u624B\u4E0A\u7684\u4E00\u4E2A\u5B89\u9632\u7C7B\u7684\u4E0A\u7EBF\u9879\u76EE\uFF0C\u505A\u7684 Java \u540E\u7AEF\u5DE5\u4F5C\u3002\u5076\u5C14\u81EA\u5DF1\u5728\u4E2A\u4EBA\u7F51\u7AD9\u5199\u535A\u5BA2\uFF0C\u5728\u6821\u4E5F\u901A\u8FC7\u4E86 CET6\uFF0C\u79D1\u7814\u505A\u4E86\u4E00\u4E2A\u4EFB\u52A1\u8C03\u5EA6\u7B97\u6CD5\u7814\u7A76\u3002</p><p>\u6625\u62DB\u4E24\u4E2A\u6708\u9762\u8BD5\u4E86\u591A\u5BB6\u5934\u90E8\u4E92\u8054\u7F51\u516C\u53F8\u7684 Java \u5F00\u53D1\u5C97\uFF0C\u5E76\u6536\u83B7 \u6DF1\u5733\u817E\u8BAF PCG \u548C \u676D\u5DDE\u963F\u91CC\u4E91 \u7684 offer\u3002\u6211\u9009\u62E9\u4E86\u540E\u8005\uFF0C\u56E0\u4E3A\u676D\u5DDE\u8FD9\u4E2A\u57CE\u5E02\u66F4\u52A0\u9002\u5408\u6211\u3002\u4ECA\u5E74\u8FD9\u4E24\u5BB6\u5B9E\u4E60 hc \u589E\u52A0\u4E86\u5DE8\u591A\uFF0C\u4E5F\u5C31\u610F\u5473\u7740\u9762\u8BD5\u96BE\u5EA6\u964D\u4F4E\uFF0C\u5E76\u4E14\u5B9E\u4E60\u5DE5\u8D44\u4E5F\u6709\u660E\u663E\u4E0A\u5347\u3002\u4E2A\u4EBA\u5206\u6790\u662F\u540E\u75AB\u60C5\u65F6\u4EE3\u6062\u590D\u7ECF\u6D4E\u53D1\u5C55\uFF0C\u516C\u53F8\u8FDB\u884C\u4E1A\u52A1\u6269\u5F20\uFF0C\u5BF9\u4EBA\u624D\u7684\u9700\u6C42\u8981\u6BD4\u5F80\u5E74\u9AD8\u3002\u7B97\u662F\u5E78\u8FD0\u5730\u5403\u5230\u4E86\u5927\u5C40\u52BF\u7684\u7EA2\u5229\u3002</p><h3 id="\u4E00\u3001\u5C97\u4F4D\u9009\u62E9" tabindex="-1"><a class="header-anchor" href="#\u4E00\u3001\u5C97\u4F4D\u9009\u62E9" aria-hidden="true">#</a> \u4E00\u3001\u5C97\u4F4D\u9009\u62E9</h3><blockquote><p>\u4E3A\u4EC0\u4E48\u9009\u62E9 CS\uFF1F</p></blockquote><p>\u5F15\u7528\u674E\u667A\u6167\u524D\u8F88\u300A\u5927\u578B\u7F51\u7AD9\u6280\u672F\u67B6\u6784\u300B\u4E00\u4E66\u7684\u540E\u8BB0\u90E8\u5206\u5BF9\u4E8E\u4E92\u8054\u7F51\u7684\u63CF\u8FF0\uFF1A</p><blockquote><p>\u4E92\u8054\u7F51\u6CA1\u6709\u95E8\u69DB\uFF0C\u8C01\u90FD\u53EF\u4EE5\u8FDB\u6765\u73A9\uFF0C\u4F46\u662F\u8FDB\u6765\u540E\uFF0C\u6700\u597D\u628A\u90A3\u4E9B\u9648\u65E7\u7684\u601D\u60F3\u548C\u5305\u88B1\u653E\u4E0B\uFF0C\u91CD\u65B0\u6765\u8FC7\u3002<br><br> \u4E92\u8054\u7F51\u662F\u4E00\u4E2A\u5F00\u653E\u548C\u5206\u4EAB\u7684\u4E16\u754C\uFF0C\u8FD9\u91CC\u662F\u521B\u65B0\u8005\u7684\u4E50\u56ED\uFF0C\u63A2\u9669\u8005\u7684\u5904\u5973\u5730\u3002\u53EA\u8981\u4F60\u52AA\u529B\uFF0C\u5BCC\u6709\u60F3\u8C61\u529B\u548C\u806A\u660E\u624D\u667A\uFF0C\u80FD\u4E3A\u7528\u6237\u521B\u9020\u4EF7\u503C\uFF0C\u80FD\u63A8\u52A8\u793E\u4F1A\u8FDB\u6B65\uFF0C\u4E0D\u7BA1\u4F60\u5F00\u59CB\u65F6\u591A\u4E48\u5F31\u5C0F\uFF0C\u603B\u6709\u673A\u4F1A\u8FC5\u901F\u805A\u96C6\u8D44\u91D1\u3001\u4EBA\u624D\u548C\u6CE8\u610F\u529B\uFF0C\u5728\u5F88\u77ED\u7684\u65F6\u95F4\u5185\u53D1\u5C55\u58EE\u5927\u3002<br><br> \u4E92\u8054\u7F51\u662F\u4E00\u79CD\u7CBE\u795E\uFF0C\u4E00\u79CD\u5F00\u653E\u3001\u5206\u4EAB\u3001\u81EA\u7531\u7684\u7CBE\u795E\uFF1B\u8D8A\u662F\u4ED8\u51FA\u4E0D\u95EE\u56DE\u62A5\uFF0C\u8D8A\u662F\u83B7\u5F97\u4E30\u539A\u7684\u56DE\u62A5\uFF1B\u8D8A\u662F\u4E0D\u8BBE\u8FB9\u754C\uFF0C\u8D8A\u662F\u62E5\u6709\u6574\u4E2A\u4E16\u754C\u3002\u4E92\u8054\u7F51\u662F\u4E00\u79CD\u98A0\u8986\uFF0C\u6253\u788E\u6240\u6709\u7684\u85E9\u7BF1\uFF0C\u7ED9\u6240\u6709\u4EBA\u5E73\u7B49\u8868\u8FBE\u548C\u83B7\u53D6\u7684\u673A\u4F1A\uFF0C\u6BCF\u4E2A\u4EBA\u90FD\u53EF\u4EE5\u53D1\u51FA\u81EA\u5DF1\u7684\u58F0\u97F3\u3002\u4E92\u8054\u7F51\u662F\u4E00\u79CD\u5EFA\u8BBE\uFF0C\u91CD\u5851\u4EBA\u4EEC\u7684\u601D\u7EF4\u65B9\u5F0F\u548C\u793E\u4F1A\u8FD0\u884C\u65B9\u5F0F\uFF0C\u5EFA\u8BBE\u4E00\u4E2A\u4EBA\u548C\u4EBA\u5F7C\u6B64\u7406\u89E3\u4FE1\u4EFB\u7684\u5927\u540C\u4E16\u754C\u3002<br><br> \u4E92\u8054\u7F51\u6B63\u5728\u5E76\u5C06\u7EE7\u7EED\u6539\u53D8\u8FD9\u4E2A\u4E16\u754C\uFF0C\u4E00\u5207\u624D\u521A\u521A\u5F00\u59CB\uFF0C\u4F60\u6211\u6B63\u751F\u9022\u5176\u65F6\uFF01</p></blockquote><p>\u6211\u8BA4\u4E3A\u8EAB\u5904\u5728\u4E92\u8054\u7F51\u65F6\u4EE3\uFF0C\u62E5\u6709\u4FE1\u606F\u5373\u62E5\u6709\u4E86\u4E00\u5207\u3002\u79EF\u6781\u62E5\u62B1\u53D8\u5316\uFF0C\u4F7F\u7528\u8BA1\u7B97\u673A\u6280\u672F\u6539\u53D8\u4E16\u754C\uFF0C\u63D0\u9AD8\u751F\u4EA7\u529B\uFF0C\u63A8\u52A8\u4EBA\u7C7B\u8FDB\u6B65\uFF0C\u662F\u6781\u5177\u4EF7\u503C\u7684\u4E8B\u4E1A\u3002\u8FD9\u4EFD\u7406\u60F3\u4E5F\u662F\u6211\u524D\u8FDB\u7684\u52A8\u529B\u3002\u52A0\u6CB9\uFF0C\u6280\u672F\u4EBA\uFF01</p><blockquote><p>\u4E3A\u4EC0\u4E48\u9009\u62E9 Java \u5F00\u53D1?</p></blockquote><p>\u6211\u4E2A\u4EBA\u89C9\u5F97\u7814\u53D1\u5DE5\u7A0B\u5E08\u8FD9\u4E2A\u804C\u4E1A\uFF0C\u80FD\u591F\u7528\u81EA\u5DF1\u7684\u4EE3\u7801\uFF0C\u521B\u9020\u7A0B\u5E8F\uFF0C\u6700\u7EC8\u5F62\u6210\u5B9E\u5B9E\u5728\u5728\u7684\u4EA7\u54C1\uFF0C\u5F88\u6709\u6210\u5C31\u611F\u3002</p><p>\u5728\u5DE5\u7A0B\u9879\u76EE\u5F00\u53D1\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u7A0B\u5E8F\u5458\u5C31\u662F\u67D0\u4E2A\u529F\u80FD\u7684\u4E3B\u4EBA\uFF0C\u7528\u5404\u79CD\u4EE3\u7801\u5B9E\u73B0\uFF0C\u6211\u5F88\u4EAB\u53D7\u8FD9\u4E2A\u521B\u9020\u7684\u8FC7\u7A0B\u3002\u5F15\u7528\u6211\u817E\u8BAF\u4E00\u9762\u65F6\u9762\u8BD5\u5B98\u7684\u89C2\u70B9\uFF1A\u540E\u7AEF\u5F00\u53D1\u5C97\u505A\u7684\u4E8B\u5C31\u662F\u89E3\u51B3\u4E1A\u52A1\u95EE\u9898\uFF0C\u91CD\u8981\u7684\u662F\u505A\u9009\u62E9\uFF0C\u9009\u62E9\u6700\u9002\u5408\u4E1A\u52A1\u7684\u6280\u672F\u3002</p><p>\u73B0\u5B9E\u5C42\u9762\uFF0CJava \u5F00\u53D1\u5E02\u9762\u4E0A\u7684\u5360\u6709\u7387\u9AD8\uFF0C\u5927\u591A\u6570\u4E1A\u52A1\u5F00\u53D1\u4F7F\u7528 Java\uFF0C\u610F\u5473\u7740\u5C97\u4F4D\u6570\u91CF\u591A\u3002\u540C\u65F6\uFF0CJava \u5B66\u4E60\u8D44\u6E90\u591A\uFF0C\u793E\u533A\u6D3B\u8DC3\uFF0C\u4FBF\u4E8E\u5B66\u4E60\u3002\u53E6\u5916\uFF0CJava \u6709\u6210\u719F\u7684\u4E00\u5957\u4F53\u7CFB\uFF0C\u6280\u672F\u6808\u5B8C\u6574\uFF0C\u5E02\u9762\u4E0A\u73B0\u6709\u4E1A\u52A1\u5BF9 Java \u7684\u4F9D\u8D56\u5EA6\u9AD8\u3002</p><p>Guide \u54E5 : \u76EE\u524D\uFF0C\u867D\u7136\u6709\u5404\u79CD\u7F16\u7A0B\u8BED\u8A00\u5F02\u519B\u7A81\u8D77\uFF0C\u4F46\u662F Java \u5E9E\u5927\u7684\u751F\u6001\u5728\u4F01\u4E1A\u5F00\u53D1\u9886\u57DF\u8FD8\u662F\u5F88\u96BE\u88AB\u64BC\u52A8\u7684\uFF01\u5E76\u4E14\uFF0C Java \u81EA\u8EAB\u4E5F\u5728\u8D8A\u6765\u8D8A\u597D\uFF01</p><h3 id="\u5B66\u4E60\u7ECF\u5386" tabindex="-1"><a class="header-anchor" href="#\u5B66\u4E60\u7ECF\u5386" aria-hidden="true">#</a> \u5B66\u4E60\u7ECF\u5386</h3><blockquote><p>\u5E78\u8FD0\u53C8\u8FF7\u832B\u7684\u5F00\u59CB</p></blockquote><p>\u6211\u5F00\u59CB\u9762\u5411\u5C31\u4E1A\u5B66\u4E60\u8BA1\u7B97\u673A\u77E5\u8BC6\uFF0C\u662F\u5728\u672C\u79D1\u5BA4\u53CB\u7684\u63A8\u8350\u4E0B\uFF0C\u627E\u5230\u4E86 CS-Notes \u8FD9\u4E2A GitHub \u4ED3\u5E93\uFF0C\u4ECE\u6B64\u5F00\u542F\u4E86\u65B0\u4E16\u754C\u7684\u5927\u95E8\u3002</p><p>\u5BA4\u53CB\u8FD8\u540C\u65F6\u63A8\u8350\u6211\u627E Java \u5F00\u53D1\u5C97\u7684\u5DE5\u4F5C\uFF0C\u76F8\u5BF9\u4ED6\u627E\u7684 C++\u8981\u7B80\u5355\u4E0D\u5C11\u3002\u5BA4\u53CB\u8FD8\u63A8\u8350\u6211\u591A\u5237\u7B97\u6CD5\u9898\uFF0C\u63A8\u8350\u6211\u4F7F\u7528 MacBook \u63D0\u9AD8\u5B66\u4E60\u6548\u7387\u3002\u6709\u4E00\u4F4D\u597D\u53CB\u4F5C\u4E3A\u5F15\u8DEF\u4EBA\uFF0C\u771F\u7684\u975E\u5E38\u5E78\u8FD0\u3002</p><p>\u8FF7\u832B\u662F\u5728\u6211\u8FDB\u5165\u5230\u4E00\u4E9B\u793E\u533A\u548C\u5B66\u4E60\u4EA4\u6D41\u7FA4\u4E2D\uFF0C\u9762\u5BF9\u6D77\u91CF\u7684\u5B66\u4E60\u8D44\u6E90\u548C\u5404\u79CD\u8A00\u8BBA\uFF0C\u89C9\u5F97\u5F88\u96BE\u7504\u522B\u51FA\u4F18\u8D28\u7684\u4FE1\u606F\u3002\u56E0\u4E3A\u7F3A\u4E4F\u7ECF\u9A8C\uFF0C\u4EA7\u751F\u7684\u95EE\u9898\u4E00\u76F4\u5806\u79EF\uFF0C\u65E0\u6CD5\u987A\u5229\u89E3\u51B3\u95EE\u9898\u7684\u5931\u843D\u611F\uFF0C\u751A\u81F3\u4E00\u5EA6\u8BA9\u6211\u5BF9\u5B66\u4E60\u4EA7\u751F\u6392\u65A5\u3002\u5F53\u65F6\u8FD8\u662F\u5B89\u6170\u81EA\u5DF1\u8F66\u5230\u5C71\u524D\u5FC5\u6709\u8DEF\uFF0C\u575A\u6301\u575A\u6301\u8FD8\u662F\u575A\u6301\uFF0C\u52AA\u529B\u63D0\u9AD8\u81EA\u5DF1\u773C\u754C\uFF0C\u5E76\u53CA\u65F6\u6E05\u7406\u81EA\u5DF1\u7684\u8D1F\u9762\u60C5\u7EEA\u3002</p><blockquote><p>\u80FD\u529B\u7A33\u5B9A\u63D0\u5347\u7684\u9636\u6BB5</p></blockquote><p>\u540E\u6765\uFF0C\u5728 GitHub \u4E0A\u627E\u5230\u4E86 JavaGuide\uFF0C\u4E2A\u4EBA\u8BA4\u4E3A\u662F Java \u9886\u57DF\u6574\u7406\u6700\u5168\u9762\u7684\u4E00\u4E2A\u4ED3\u5E93\uFF0C\u6211\u7CFB\u7EDF\u5730\u5B66\u5230\u4E86\u5B66\u4E60\u8DEF\u5F84\uFF0C\u5B66\u4E60\u65B9\u6CD5\uFF0C\u4F5C\u4E3A\u7A0B\u5E8F\u5458\u5E94\u8BE5\u5177\u6709\u7684\u826F\u597D\u4E60\u60EF\u7B49\u7B49\u3002\u6211\u8FD8\u5173\u6CE8\u4E86\u4E8C\u54E5\u7684\u516C\u4F17\u53F7\uFF0C\u770B\u4E86\u8BB8\u8BB8\u591A\u591A\u4E8C\u54E5\u539F\u521B\u7684\u4F18\u8D28\u6587\u7AE0\u3002</p><p>\u8FDB\u5165\u7814\u7A76\u751F\u9636\u6BB5\u540E\uFF0C\u5728\u5B9E\u9A8C\u5BA4\uFF0C\u5B66\u751F\u4FF1\u4E50\u90E8\uFF0C\u5404\u79CD\u6D3B\u52A8\u6BD4\u8D5B\uFF0C\u516C\u53F8\u7684\u5BA3\u8BB2\u4F1A\u7B49\uFF0C\u90FD\u80FD\u5B66\u5230\u5F88\u591A\u4E1C\u897F\uFF0C\u8BA4\u8BC6\u5F88\u591A\u670B\u53CB\u3002\u6211\u8BA4\u4E3A\u6709\u5FD7\u540C\u9053\u5408\u7684\u670B\u53CB\u4E00\u8D77\u5B66\u4E60\uFF0C\u4E92\u76F8\u67E5\u6F0F\u8865\u7F3A\uFF0C\u5E2E\u5FD9\u89E3\u51B3\u95EE\u9898\uFF0C\u76F8\u5BF9\u6211\u4E4B\u524D\u7684\u81EA\u5B66\uFF0C\u6548\u7387\u4E0A\u8981\u63D0\u9AD8\u4E0D\u5C11\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/mianjing/shanganaliyun-2.png" alt="" loading="lazy"></p><p>\u5728\u4E86\u89E3\u5230\u9879\u76EE\u5728\u9762\u8BD5\u4E2D\u5360\u636E\u6BD4\u8F83\u91CD\u8981\u7684\u4F4D\u7F6E\uFF0C\u6211\u4F1A\u5728\u5B66\u4E60\u5B8C\u7406\u8BBA\u77E5\u8BC6\u540E\uFF0C\u5C3D\u91CF\u7528\u4EE3\u7801\u5B9E\u8DF5\u3002\u5728\u81EA\u5DF1\u5B66\u4E60\u9879\u76EE\u89C6\u9891\u6559\u7A0B\uFF0C\u5E76\u8DDF\u7740\u624B\u6253\u8FC7\u540E\uFF0C\u6211\u53C2\u4E0E\u5230\u5B66\u6821\u8001\u5E08\u624B\u4E0B\u7684\u4E00\u4E2A\u4E0A\u7EBF\u9879\u76EE\uFF0C\u771F\u6B63\u7528\u6280\u672F\u89E3\u51B3\u5B9E\u9645\u95EE\u9898\u3002</p><blockquote><p>\u6625\u62DB\u9762\u8BD5\u9636\u6BB5</p></blockquote><p>\u867D\u7136\u5B66\u4E60\u5F00\u59CB\u5C31\u76EE\u6807\u5BF9\u51C6\u627E\u5DE5\u4F5C\uFF0C\u4F46\u662F\u6CA1\u6709\u5B9E\u9645\u9762\u8BD5\u7ECF\u9A8C\uFF0C\u8FD8\u662F\u8BA9\u6211\u5BF9\u4E8E\u80FD\u53D6\u5F97\u600E\u4E48 offer \u5927\u5927\u5B58\u7591\u3002\u5F00\u59CB\u9762\u8BD5\u524D\u6211\u4E5F\u505A\u597D\u4E86\u62FF\u5230\u4E0D\u6EE1\u610F offer \u7684\u5FC3\u7406\u51C6\u5907\uFF0C\u5728\u6295\u7B80\u5386\u4E0A\u4E5F\u5C3D\u91CF\u6D77\u6295\uFF0C\u4E89\u53D6\u591A\u4E00\u4E9B\u9762\u8BD5\u673A\u4F1A\u3002</p><p>\u6700\u5148\u63A5\u5230\u7684\u6765\u81EA\u963F\u91CC\u6570\u636E\u5E93\u90E8\u95E8\u7684\u63D0\u524D\u6279\u7535\u8BDD\u9762\u8BD5\uFF0C\u6211\u5F53\u65F6\u8FD8\u5904\u4E8E\u521A\u5F00\u59CB\u590D\u4E60\u4E4B\u524D\u5B66\u4E60\u5185\u5BB9\u7684\u9636\u6BB5\uFF0C\u800C\u4E14\u9762\u5BF9\u7684\u662F\u963F\u91CC\uFF0C\u81EA\u7136\u662F\u76F8\u5F53\u7684\u4E0D\u81EA\u4FE1\u3002\u6211\u8FD8\u662F\u54AC\u54AC\u7259\u4E0A\u4E86\uFF0C\u786C\u7740\u5934\u76AE\u9762\u4E86 85 \u5206\u949F\uFF0C\u88AB\u9762\u8BD5\u5B98\u75AF\u72C2\u8D28\u7591\u9879\u76EE\u4E2D\u5F88\u7EC6\u8282\u7684\u70B9\uFF0C\u5F53\u65F6\u4E5F\u662F\u53D7\u5230\u4E86\u5F88\u5927\u6253\u51FB\u3002\u4E0D\u8FC7\u597D\u5728\u6211\u6CA1\u6709\u5F88\u5927\u671F\u671B\uFF0C\u800C\u4E14\u505A\u4E86\u88AB\u540A\u6253\u7684\u51C6\u5907\uFF0C\u5176\u5B9E\u5728\u5BF9\u81EA\u8EAB\u9762\u8BD5\u7ECF\u9A8C\u7684\u589E\u957F\u4E0A\uFF0C\u8FD8\u662F\u5229\u5927\u4E8E\u5F0A\u7684\uFF0C\u6BD5\u7ADF\u540E\u7EED\u4E5F\u6CA1\u6709\u5176\u4ED6\u9762\u8BD5\u80FD\u6BD4\u8FD9\u6B21\u66F4\u52A0\u6253\u51FB\u5230\u6211\u4E86\uFF1A\uFF09\u3002</p><p>\u968F\u540E\u6B63\u5F0F\u6279\u5F00\u59CB\uFF0C\u9646\u7EED\u9762\u4E86\u817E\u8BAF PCG\uFF0C\u7F8E\u56E2\uFF0C\u7F51\u6613\u4E92\u8054\u7F51\uFF0C\u5B57\u8282\uFF0C\u963F\u91CC\u8FD9\u51E0\u5BB6\u5934\u90E8\u5927\u5382\u3002\u817E\u8BAF\u7684\u9762\u8BD5\u7ECF\u5386\u7ED9\u4E86\u6211\u83AB\u5927\u7684\u9F13\u52B1\uFF0C\u6211\u611F\u5230\u975E\u5E38\u5E78\u8FD0\u80FD\u591F\u5F97\u5230\u817E\u8BAF\u7684\u8BA4\u53EF\uFF0C\u7279\u522B\u611F\u8C22\u4E00\u9762\u9762\u8BD5\u5B98\uFF0C\u5728\u7EBF\u4E0B\u9762\u8BD5\u4E2D\u7ED9\u4E88\u6211\u7684\u80AF\u5B9A\uFF0C\u4EE5\u53CA\u5BF9\u4E8E\u5728\u540E\u7AEF\u5F00\u53D1\u65B9\u5411\u4E0A\u7684\u6559\u5BFC\u3002\u4ECE\u817E\u8BAF\u4E00\u9762\uFF0C\u5230 oc\uFF0C\u603B\u5171 6 \u5929\uFF0C\u800C\u4E14\u6211\u7B14\u8BD5 0ac \u4E5F\u987A\u5229\u901A\u8FC7\uFF0C\u4E0D\u5F97\u4E0D\u8BF4\u817E\u8BAF\u4ECA\u5E74 hc \u4E4B\u591A\uFF0C\u96BE\u5EA6\u4E4B\u4F4E\uFF0C\u6D41\u7A0B\u4E4B\u5FEB\u3002\u76F8\u5BF9\u5B9E\u9A8C\u5BA4\u5176\u4ED6\u540C\u5B66\uFF0C\u6211 3.22 \u5C31\u4E0A\u5CB8\u817E\u8BAF\uFF0C\u6211\u540E\u7EED\u7684\u590D\u4E60\u548C\u9762\u8BD5\u8F7B\u677E\u4E86\u5F88\u591A\u3002</p><p>\u91CD\u70B9\u804A\u4E0B\u7F51\u6613\uFF0C\u6211\u662F hr \u9762\u540E\u62D6\u4E86 20 \u5929\uFF0C\u7136\u540E\u6302\u4E86\uFF0C\u539F\u56E0\u662F\u9700\u8981\u6A2A\u5411\u5BF9\u6BD4\uFF0C\u4F18\u5148\u5F55\u53D6\u66F4\u4F18\u79C0\u7684\u540C\u5B66\u3002\u5B9E\u4E60\u62DB\u8058\u5C31\u6709\u8FD9\u79CD\u60C5\u51B5\uFF0C\u79CB\u62DB\u7684\u6218\u573A\u6050\u6015\u66F4\u52A0\u6B8B\u9177\uFF0C\u56E0\u6B64\u6211\u5EFA\u8BAE\u6709\u7CBE\u529B\u3001\u51C6\u5907\u5C1A\u53EF\u7684\u540C\u5B66\uFF0C\u5728 7 8 \u6708\u7684\u63D0\u524D\u6279\u5C31\u5C3D\u65E9\u6295\u9012\u7B80\u5386\uFF0C\u7ED9\u81EA\u5DF1\u591A\u4E9B\u673A\u4F1A\u548C\u9009\u62E9\u7684\u7A7A\u95F4\u3002</p><p>\u5728 3 \u6708\u5E95\u6302\u4E86\u963F\u91CC AE \u90E8\u95E8\u540E\uFF0C4 \u6708\u4E2D\u65EC\u5F00\u59CB\u518D\u5EA6\u6311\u6218\u963F\u91CC\u4E91\u5168\u7403\u6280\u672F\u670D\u52A1\u90E8\uFF0C\u8FD9\u4E5F\u662F\u6211\u8FD9\u6B21\u6625\u62DB\u6700\u540E\u4E00\u4E2A\u90E8\u95E8\u7684\u9762\u8BD5\u3002\u53EA\u80FD\u8BF4\u5728\u7ECF\u5386\u591A\u4E2A\u5927\u5382\u7684\u9762\u8BD5\u6D17\u793C\u540E\uFF0C\u6211\u4E5F\u80FD\u6BD4\u8F83\u6E38\u5203\u6709\u4F59\u5730\u901A\u8FC7\u963F\u91CC\u7684\u9762\u8BD5\uFF0C\u800C\u4E14\u76F8\u5BF9\u4E8E\u7F51\u6613\uFF0C\u5728\u6700\u540E\u6392\u5E8F\u5F55\u53D6\u73AF\u8282\uFF0C\u6211\u5E78\u8FD0\u5730\u6536\u5230 offer\u3002\u5728 5.10\uFF0C\u6211\u7684\u6625\u62DB\u4E5F\u7EC8\u4E8E\u4EE5\u610F\u5916\u6EE1\u610F\u7684\u7ED3\u5C40\u7ED3\u675F\u3002</p><p>\u6700\u540E\u603B\u7ED3\u4E0B\u6211\u5BF9\u6295\u9012\u90E8\u95E8\u7684\u5EFA\u8BAE \uFF1A\u4E00\u5B9A\u8981\u52A0\u4E0A\u90E8\u95E8\u5185\u90E8\u524D\u8F88\u7684\u8054\u7CFB\u65B9\u5F0F\uFF0C\u53CA\u65F6\u8BE2\u95EE\u9762\u8BD5\u53CD\u9988\u548C\u8FDB\u5EA6\u3002\u540C\u65F6\u8981\u52A0\u90E8\u95E8\u7684\u62DB\u8058\u7FA4\uFF0C\u6216\u8005\u4EA4\u6D41\u7FA4\uFF0C\u53CA\u65F6\u4E86\u89E3\u653F\u7B56\u53D8\u52A8\uFF0C\u5176\u4ED6\u540C\u5B66\u662F\u5426\u6536\u5230\u9762\u8BD5\u901A\u77E5\u4E43\u81F3 offer \u7B49\uFF0C\u8FD9\u4E9B\u4FE1\u606F\u975E\u5E38\u91CD\u8981\u3002</p><h3 id="\u5B66\u4E60\u65B9\u6CD5\u7684\u5EFA\u8BAE" tabindex="-1"><a class="header-anchor" href="#\u5B66\u4E60\u65B9\u6CD5\u7684\u5EFA\u8BAE" aria-hidden="true">#</a> \u5B66\u4E60\u65B9\u6CD5\u7684\u5EFA\u8BAE</h3><ul><li>\u5F17\u66FC\u5B66\u4E60\u6CD5\u3002</li><li>\u6CE8\u610F\u6548\u7387\uFF0C\u901F\u5EA6\u8981\u5FEB\u3002</li><li>\u6CE8\u610F\u5C06\u7406\u8BBA\u77E5\u8BC6\uFF0C\u7528\u4EE3\u7801\u5B9E\u8DF5\u3002</li><li>\u6BCF\u65E5\u6BCF\u5468\u6BCF\u6708\u521D\uFF0C\u89C4\u5212\u5B66\u4E60\u7684\u5185\u5BB9\u3002</li><li>\u7ED9\u9700\u8981\u5B66\u4E60\u7684\u5185\u5BB9\u6E05\u695A\u969C\u788D\uFF0C\u4E13\u6CE8\u5230\u4E00\u5757\u5185\u5BB9\u4E0A\uFF0C\u5E76\u4FDD\u8BC1\u5B66\u4E60\u7684\u8FDE\u7EED\u6027\u3002</li><li>\u591A\u8054\u60F3\u76F8\u5173\u7684\u77E5\u8BC6\u70B9\uFF0C\u63D0\u9AD8\u77E5\u8BC6\u8FC1\u79FB\u80FD\u529B\u3002</li><li>\u52E4\u505A\u7B14\u8BB0\uFF0C\u6CE8\u610F\u603B\u7ED3\u548C\u56DE\u987E\u3002</li></ul><h3 id="\u5FC3\u6001\u7684\u5EFA\u8BAE" tabindex="-1"><a class="header-anchor" href="#\u5FC3\u6001\u7684\u5EFA\u8BAE" aria-hidden="true">#</a> \u5FC3\u6001\u7684\u5EFA\u8BAE</h3><ul><li>\u8981\u8B66\u60D5\u7F51\u4E0A\u6216\u8005\u8EAB\u8FB9\u4EBA\u6240\u8D29\u5356\u7684\u7126\u8651\u3002</li><li>\u53D6\u5F97\u8FDB\u6B65\u9700\u8981\u6BCF\u5929\u524D\u8FDB\u5728\u6B63\u786E\u7684\u65B9\u5411\uFF0C\u5E76\u4E14\u5C3D\u81EA\u5DF1\u6700\u5927\u7684\u52AA\u529B\u3002</li><li>\u5BF9\u5F85\u81EA\u8EAB\u73B0\u72B6\u8981\u5B9E\u4E8B\u6C42\u662F\u3002\u73B0\u5728\u5B9E\u529B\u4E0D\u591F\u4E5F\u4E0D\u7528\u7070\u5FC3\uFF0C\u53EA\u80FD\u8BF4\u660E\u8FD8\u6CA1\u5230\u6700\u540E\uFF0C\u6700\u540E\u90FD\u4F1A\u6709\u5149\u660E\u7684\u672A\u6765\u3002</li><li>\u5FC3\u6025\u5403\u4E0D\u4E86\u70ED\u8C46\u8150\uFF0C\u4E00\u6B65\u4E00\u4E2A\u811A\u5370\uFF0C\u8E0F\u8E0F\u5B9E\u5B9E\u5730\u505A\u4E8B\u3002</li></ul><h3 id="\u5173\u4E8E\u8BFB\u7814" tabindex="-1"><a class="header-anchor" href="#\u5173\u4E8E\u8BFB\u7814" aria-hidden="true">#</a> \u5173\u4E8E\u8BFB\u7814</h3><p>\u6211\u672C\u4EBA\u662F\u5F3A\u70C8\u63A8\u8350\u5404\u4F4D\u672C\u79D1\u540C\u5B66\u8BFB\u7814\u3002</p><blockquote><p>\u4E3A\u4EC0\u4E48\u5462\uFF1F</p></blockquote><ul><li><strong>\u8BFB\u7814\u671F\u95F4\u4F1A\u6709\u5927\u91CF\u7684\u65F6\u95F4</strong> \uFF1A\u4E2A\u4EBA\u8BA4\u4E3A\uFF0C\u50CF\u6211\u4E00\u6837\u7684\u591A\u6570\u540C\u5B66\uFF0C\u5728\u7ECF\u5386\u4EE5\u9AD8\u8003\u5206\u6570\u4E3A\u76EE\u6807\u7684\u5E94\u8BD5\u6559\u80B2\u540E\uFF0C\u672C\u79D1\u9636\u6BB5\u9762\u5BF9\u5168\u65B0\u7684\u4E13\u4E1A\u77E5\u8BC6\u548C\u5B66\u4E60\u73AF\u5883\uFF0C\u4EE5\u53CA\u6765\u81EA\u4E92\u8054\u7F51\u548C\u8EAB\u8FB9\u5404\u79CD\u4FE1\u606F\uFF0C\u611F\u5230\u81EA\u7531\u4E14\u8FF7\u832B\u3002\u8FD9\u5F88\u6B63\u5E38\uFF0C\u6211\u4EEC\u9700\u8981\u7684\u662F\u65F6\u95F4\u53BB\u627E\u5230\u5408\u9002\u7684\u65B9\u5411\uFF0C\u5F62\u6210\u6709\u6548\u7684\u5B66\u4E60\u65B9\u6CD5\u3002</li><li><strong>\u66F4\u591A\u7684\u673A\u4F1A</strong> \uFF1A\u8003\u7814\u6709\u673A\u4F1A\u8FDB\u5165\u66F4\u597D\u7684\u5B66\u6821\uFF0C\u9009\u62E9\u66F4\u9002\u5408\u7684\u4E13\u4E1A\u3002\u773C\u754C\u7684\u63D0\u5347\uFF0C\u7ED3\u4EA4\u66F4\u4F18\u8D28\u7684\u4EBA\u8109\u4E5F\u5F88\u91CD\u8981\uFF0C\u63D0\u9AD8\u672A\u6765\u53D1\u5C55\u7684\u4E0A\u9650\u3002\u597D\u7684\u5BFC\u5E08\u80FD\u591F\u63D0\u4F9B\u597D\u7684\u7814\u7A76\u65B9\u5411\uFF0C\u597D\u7684\u9879\u76EE\uFF0C\u5E2E\u52A9\u6307\u5BFC\u79D1\u7814\u3002\u751A\u81F3\u80FD\u63A8\u8350\u5DE5\u4F5C\u548C\u8BFB\u535A\u6DF1\u9020\u3002</li><li><strong>\u7B80\u5386\u52A0\u5206</strong> \uFF1A\u4E92\u8054\u7F51\u884C\u4E1A\u7684\u53D1\u5C55\u901F\u5EA6\u5728\u51CF\u7F13\uFF0C\u4F46\u4ECE\u4E1A\u4EBA\u5458\u589E\u901F\u4F9D\u65E7\uFF0C\u610F\u5473\u7740\u66F4\u52A0\u6FC0\u70C8\u7684\u7ADE\u4E89\u3002\u66F4\u9AD8\u7684\u5B66\u5386\uFF0C\u66F4\u5F3A\u7684\u4E13\u4E1A\u80FD\u529B\uFF0C\u5DF2\u7ECF\u6210\u4E3A\u4F01\u4E1A\u5BF9\u5019\u9009\u4EBA\u7684\u8981\u6C42\u3002</li><li><strong>\u79D1\u7814\u5185\u5BB9\u5BF9\u80FD\u529B\u7684\u57F9\u517B</strong> : \u79D1\u7814\u4E3B\u8981\u662F\u5BF9\u7B97\u6CD5\u7684\u5B66\u4E60\u548C\u521B\u65B0\uFF0C\u80FD\u57F9\u517B\u8BBE\u8BA1\u80FD\u529B\u3002\u5BF9\u4E8E\u8BBA\u6587\u7684\u7814\u7A76\uFF0C\u57F9\u517B\u7406\u89E3\u80FD\u529B\u548C\u5916\u6587\u7684\u9605\u8BFB\u80FD\u529B\u3002\u8FD9\u4E9B\u80FD\u529B\u90FD\u6709\u52A9\u4E8E\u672A\u6765\uFF0C\u628A\u5DE5\u4F5C\u505A\u5F97\u66F4\u52A0\u51FA\u8272\u3002</li></ul><p>\u4E8C\u54E5 : \u6BD4\u8F83\u4E2D\u80AF\u7684\u5EFA\u8BAE\uFF01\u4E0D\u8FC7\uFF0C\u4E5F\u4E0D\u8981\u5FFD\u7565\u4E0D\u8BFB\u7814\u5E26\u6765\u7684\u51E0\u5E74\u5DE5\u4F5C\u7ECF\u9A8C\u5BF9\u81EA\u5DF1\u7684\u6536\u76CA\u3002\u662F\u5426\u8BFB\u7814\uFF0C\u6839\u636E\u81EA\u8EAB\u60C5\u51B5\u505A\u9009\u62E9\u5373\u53EF\uFF01</p><h3 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> \u603B\u7ED3</h3><p>\u4E0D\u5FD8\u521D\u5FC3\uFF0C\u7825\u783A\u524D\u884C\u3002\u613F\u5927\u5BB6\u5B66\u4E1A\u8FDB\u6B65\u3002\u795D\u5404\u4F4D\u79CB\u62DB\u53D6\u5F97\u597D\u6210\u7EE9!</p><hr><p><em>\u6CA1\u6709\u4EC0\u4E48\u4F7F\u6211\u505C\u7559\u2014\u2014\u9664\u4E86\u76EE\u7684\uFF0C\u7EB5\u7136\u5CB8\u65C1\u6709\u73AB\u7470\u3001\u6709\u7EFF\u836B\u3001\u6709\u5B81\u9759\u7684\u6E2F\u6E7E\uFF0C\u6211\u662F\u4E0D\u7CFB\u4E4B\u821F</em>\u3002</p>',49),_=a("\u8F6C\u8F7D\u94FE\u63A5\uFF1A"),f={href:"https://mp.weixin.qq.com/s/EB_NCC2fNyYHuz04k1H4Nw",target:"_blank",rel:"noopener noreferrer"},g=a("https://mp.weixin.qq.com/s/EB_NCC2fNyYHuz04k1H4Nw"),m=e("p",null,[e("img",{src:"http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/xingbiaogongzhonghao.png",alt:"",loading:"lazy"})],-1);function k(v,q){const o=r("ExternalLinkIcon");return l(),n("div",null,[s,e("p",null,[h,e("a",d,[u,t(o)])]),b,e("p",null,[_,e("a",f,[g,t(o)])]),m])}var C=i(c,[["render",k],["__file","shanganaliyun.html.vue"]]);export{C as default};
