import{_ as o}from"./plugin-vue_export-helper.21dcd24c.js";import{r as a,o as n,c as r,b as e,a as s,d as p,e as c}from"./app.9b6c14fa.js";const i={},l=e("h2",{id:"\u7814\u7A76\u6240\u6708\u5165\u4E24\u4E07-\u662F\u4EC0\u4E48\u4F53\u9A8C",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#\u7814\u7A76\u6240\u6708\u5165\u4E24\u4E07-\u662F\u4EC0\u4E48\u4F53\u9A8C","aria-hidden":"true"},"#"),p(" \u7814\u7A76\u6240\u6708\u5165\u4E24\u4E07\uFF0C\u662F\u4EC0\u4E48\u4F53\u9A8C\uFF1F")],-1),h=e("p",null,"\u5927\u5BB6\u597D\uFF0C\u6211\u662F\u4E8C\u54E5\u5440\u3002",-1),_=p("\u4E4B\u524D\u5728\u4E00\u7BC7"),d={href:"https://mp.weixin.qq.com/s/oc-6Um6y0LlpSQwDY4HAbw",target:"_blank",rel:"noopener noreferrer"},m=p("\u7F8E\u56E2\u8FD8\u662F\u7814\u7A76\u6240\uFF0C\u7F8E\u56E2\u5E74\u5305\u591A\u4E8C\u5341\u4E07\uFF0C\u600E\u4E48\u9009"),u=p("\u7684\u6587\u7AE0\u91CC\u505A\u8FC7\u4E00\u4E2A\u6295\u7968\uFF1A\u4E00\u7EBF\u4E92\u8054\u7F51\u5927\u5382\u8FD8\u662F\u4E8C\u4E09\u7EBF\u57CE\u5E02\u7684\u7814\u7A76\u6240\uFF1F"),b=c('<p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/manongshenghuo/yanjiusuo-20wan-1.png" alt="" loading="lazy"></p><p>1000 \u591A\u4EBA\u7684\u6295\u7968\u7ED3\u679C\u4E2D\uFF0C\u9009\u62E9\u7814\u7A76\u6240\u7684\u7ADF\u7136\u6BD4\u9009\u62E9\u4E92\u8054\u7F51\u7684\u591A\u4E00\u4E9B\uFF0C\u770B\u6765\u5411\u5F80\u7814\u7A76\u6240\u90A3\u79CD\u80FD\u517C\u987E\u751F\u6D3B\u548C\u5DE5\u4F5C\u7684\u5C0F\u4F19\u4F34\u4E0D\u5728\u5C11\u6570\u554A\u3002</p><p>\u600E\u4E48\u8BF4\u5462\uFF1F\u5404\u6709\u5229\u5F0A\uFF0C\u770B\u9009\u62E9\u4EC0\u4E48\u6837\u7684\u751F\u6D3B\u4E86\u3002\u8FD9\u91CC\u7ED9\u5927\u5BB6\u5206\u4EAB\u4E00\u4F4D\u7B54\u4E3B\u670B\u53CB\u5728\u7814\u7A76\u6240\u5DE5\u4F5C\u7684\u611F\u53D7\u3002\u5E0C\u671B\u5BF9\u5C0F\u4F19\u4F34\u4EEC\u6709\u4E00\u70B9\u70B9\u5E2E\u52A9\u548C\u542F\u53D1~</p><hr><blockquote><p>\u4F5C\u8005\uFF1A\u6613\u8D1D\u8D1D https://www.zhihu.com/question/64371326/answer/2251105460</p></blockquote><p>2019 \u5E74 7 \u6708\u6BD5\u4E1A\u4E8E\u5317\u822A\uFF0C\u540E\u5165\u804C\u897F\u5B89\u67D0\u6240\u3002</p><p>\u5148\u53D1\u4E2A\u6536\u5165\u622A\u56FE\u5427\u3002</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/manongshenghuo/yanjiusuo-20wan-2.png" alt="" loading="lazy"></p><p>\u9996\u5148\u58F0\u660E\u6211\u8FD9\u4E2A\u6536\u5165\u662F\u8981\u4F4E\u4E8E\u8DDF\u6211\u540C\u5E74\u4E0A\u5927\u5B66\uFF0C2014\u5E74\u7855\u58EB\u5165\u6240\u7684\u540C\u5B66\u7684\uFF0C\u6211\u6BD4\u8F83\u719F\u7684\u6280\u672F\u90E8\u95E8\u7684\u540C\u5B66\u6536\u5165\u52A0\u516C\u79EF\u91D1\u572830\u5DE6\u53F3\u4E86\uFF0C\u4E0D\u662F\u5F88\u591A\u4EBA\u60F3\u7684\u535A\u58EB\u624D\u8FD9\u4E48\u70B9\u7855\u58EB\u66F4\u5C11\u4E86\u3002\u6211\u8981\u8FBE\u5230\u8FD9\u4E2A\u6570\u5B57\u4F30\u8BA1\u8FD8\u5F97\u4E24\u5E74\u5DE6\u53F3\u3002</p><p>\u6709\u4EBA\u8BF4\u8BF4\u535A\u58EB\u624D\u8FD9\u70B9\u94B1\uFF0C\u5BF9\u4E8E\u4E0D\u4E86\u89E3\u884C\u60C5\u7684\uFF0C\u6211\u53EA\u80FD\u56DE\u7B54\uFF1A\u80FD\u8D5A\u5F88\u591A\u94B1\u662F\u4E16\u4EBA\u5BF9\u535A\u58EB\u6700\u5927\u7684\u8BEF\u89E3\uFF1B\u5BF9\u4E8E\u79C0\u4F18\u8D8A\u7684\uFF0C\u4F60\u5148\u770B\u770B\u81EA\u5DF1\u662F\u4E0D\u662F\u4E0B\u9762\u4E24\u79CD\u60C5\u51B5\u91CC\u7684\u53CD\u9762\u5178\u578B\u3002\u5BF9\u4E8E\u6211\u6765\u8BF4\uFF0C\u53EF\u80FD\u56E0\u4E3A\u662F\u7A77\u82E6\u4EBA\u5BB6\u7684\u5B69\u5B50\uFF0C\u4ECE\u5C0F\u6CA1\u89C1\u8FC7\u5565\u94B1\uFF0C\u6240\u4EE5\u89C9\u5F97\u8FD9\u4E2A\u6536\u5165\u8FD8\u53EF\u4EE5\u3002</p><p>\u8FD8\u6709\u4E00\u4E9B\u5B66\u5F1F\u4E5F\u662F\u535A\u58EB\u7EA0\u7ED3\u5F85\u9047\u95EE\u9898\uFF0C\u95EE\u6211\u6536\u5165\u4E3A\u4EC0\u4E48\u8FD9\u4E48\u5C11\uFF0C\u6211\u8FD9\u91CC\u6709\u70B9\u5FC3\u5F97\u8DDF\u4F60\u4EEC\u5206\u4EAB\u4E0B\uFF0C\u4E0D\u4E00\u5B9A\u5168\u5BF9\u3002</p><p>\u9996\u5148\u6211\u53F8\u540D\u58F0\u5728\u5916\uFF0C\u786E\u5B9E\u4E0D\u6101\u62DB\u4E0D\u5230\u4EBA\uFF0C\u6240\u4EE5\u6CA1\u6709\u5B89\u5BB6\u8D39\uFF0C\u535A\u58EB\u4E5F\u6CA1\u6709\u4EC0\u4E48\u7279\u6B8A\u7684\u7167\u987E\uFF0C\u5C31\u6309\u6B63\u5E38\u7684\u5DE5\u8D44\u4F53\u7CFB\u53D1\u94B1\u3002</p><p>\u6709\u4E9B\u5355\u4F4D\u4F1A\u7ED9\u535A\u58EB\u5B89\u5BB6\u8D39\uFF0C\u4E3A\u4EC0\u4E48\u7ED9\u5B89\u5BB6\u8D39\u4F60\u53EF\u4EE5\u7422\u78E8\u4E00\u4E0B\uFF0C\u636E\u6211\u6240\u77E5\u5317\u4EAC\u7684\u51E0\u4E2A\u5927\u603B\u4F53\u57FA\u672C\u90FD\u6CA1\u6709\u5B89\u5BB6\u8D39\uFF0C\u6216\u8005\u6709\u8DDF\u6CA1\u6709\u4E00\u6837\u3002</p><p>\u6709\u4E9B\u5355\u4F4D\u4F1A\u62D4\u9AD8\u85AA\u916C\u7ED9\u4F60\uFF0C\u8FD9\u6837\u4E00\u65B9\u9762\u662F\u53EF\u80FD\u4E0D\u80FD\u6301\u7EED\uFF0C\u6BD4\u5982\u7ED9\u4F60\u4E09\u4E94\u5E74\uFF0C\u53E6\u4E00\u65B9\u9762\u9020\u6210\u65B0\u8001\u5458\u5DE5\u5DE5\u8D44\u5012\u6302\uFF0C\u5F15\u8D77\u5185\u90E8\u77DB\u76FE\uFF0C\u5DE5\u7A0B\u9886\u57DF\u662F\u5F88\u9700\u8981\u5E08\u5F92\u4F20\u627F\u7684\uFF0C\u4E00\u4E2A\u65B0\u4EBA\u4E00\u6765\u5355\u4F4D\u5C31\u56E0\u4E3A\u5F85\u9047\u95EE\u9898\u88AB\u654C\u89C6\uFF0C\u4F60\u53EF\u4EE5\u60F3\u8C61\u4E00\u4E0B\u3002</p><p>\u800C\u4E14\u5BA2\u89C2\u8BB2\uFF0C\u6211\u4EEC\u603B\u4F53\u5355\u4F4D\u4E0D\u4F1A\u56E0\u4E3A\u4E00\u4E2A\u535A\u58EB\u6765\u4E86\u5C31\u4F1A\u6709\u4EC0\u4E48\u5927\u7684\u6280\u672F\u63D0\u5347\uFF0C\u4F60\u6765\u4E86\u4E5F\u662F\u5B66\u4E60\u3002\u6211\u6709\u65F6\u5019\u89C9\u5F97\u5355\u4F4D\u4E0D\u5ACC\u6211\u5E74\u9F84\u5927\u809D\u4E0D\u52A8\u80AF\u6536\u7559\u6211\u5C31\u8BE5\u77E5\u8DB3\u4E86\u3002</p><p>\u5BF9\u4E8E\u627E\u5DE5\u4F5C\u5C24\u5176\u662F\u4F53\u5236\u5185\u7684\u7684\u535A\u58EB\uFF0C\u5982\u679C\u4E00\u4E2A\u5355\u4F4D\u627F\u8BFA\u7ED9\u4F60\u5F88\u591A\u94B1\uFF0C\u53EF\u80FD\u662F\uFF1A</p><ul><li>1\u3001\u8FD9\u4E2A\u5355\u4F4D\u771F\u7684\u5F88\u6709\u94B1\uFF0C\u5927\u5BB6\u6536\u5165\u90FD\u5F88\u9AD8;</li><li>2\u3001\u4F60\u786E\u5B9E\u80FD\u7ED9\u5355\u4F4D\u505A\u522B\u4EBA\u505A\u4E0D\u4E86\u7684\u8D21\u732E\uFF0C\u5355\u4F4D\u5F88\u9700\u8981\u4F60\u3002</li></ul><p>\u5982\u679C\u4E0D\u662F\u8FD9\u4E24\u70B9\uFF0C\u4F60\u5C31\u8981\u8003\u8651\u662F\u4E0D\u662F\u5751\u4E86\u3002\u867D\u7136\u56FD\u4F01\u7684\u94B1\u4E0D\u662F\u4EBA\u529B\u81EA\u5DF1\u5BB6\u7684\uFF0C\u4F46\u662F\u4EBA\u529B\u60F3\u8981\u8D85\u51FA\u5DE5\u8D44\u4F53\u7CFB\u5730\u7ED9\u4F60\u62A5\u916C\u5176\u5B9E\u662F\u4EF6\u633A\u4E0D\u597D\u529E\u7684\u4E8B\uFF0C\u8FD9\u4E8B\u5728\u56FD\u4F01\u53EF\u80FD\u6BD4\u79C1\u4F01\u96BE\u5EA6\u5927\u5F97\u591A\uFF0C\u5982\u679C\u4ED6\u80FD\u6B63\u5E38\u7684\u62DB\u5230\u4ED6\u60F3\u8981\u62DB\u7684\u4F18\u79C0\u7684\u535A\u58EB\uFF0C\u4ED6\u5E72\u561B\u8981\u7ED9\u81EA\u5DF1\u627E\u4E8B\uFF0C\u8FD8\u5BB9\u6613\u5F15\u8D77\u77DB\u76FE\uFF0C\u4E3A\u4E86\u8BA9\u4F60\u8C22\u4ED6\u5417\uFF1F</p><p>\u5BF9\u4E8E\u79C1\u4F01\u903B\u8F91\u66F4\u7B80\u5355\uFF0C\u4F01\u4E1A\u903B\u8F91\u8DDF\u98CE\u6295\u4E00\u6837\uFF0C\u4ED6\u7ED9\u4F60\u4E00\u5757\u94B1\u94B1\uFF0C\u4F60\u521B\u902010\u5757\u94B1100\u5757\u94B1\u7684\u4EF7\u503C\uFF08\u6B64\u4EF7\u503C\u4E0D\u4E00\u5B9A\u662F\u9A6C\u4E0A\u770B\u5230\u7684\u7ECF\u6D4E\u5229\u76CA\uFF09\uFF0C\u4F60\u521B\u9020\u4E0D\u4E86\u5F00\u4E86\u4F60\u5C31\u662F\u4E86\u3002</p><p>\u5FC3\u5F97\u5206\u4EAB\u5B8C\u6BD5\uFF0C\u7EE7\u7EED\u6B63\u9898\u3002</p><p>\u5468\u56F4\u623F\u4EF7\u4E0D\u5230\u4E24\u4E07\uFF0C\u4E24\u516C\u91CC\u5916\u534E\u6DA6\u76D6\u5728\u897F\u5B89ccbd\u4E07\u8C61\u57CE\u65C1\u8FB9\u7684\u5927\u5E73\u5C42\u4E0D\u5230\u4E24\u4E07\u4E94\uFF0C\u5730\u6BB5\u53C2\u8003\u5317\u4EAC\u56FD\u8D38\u65C1\u8FB9\u7684\u65B0\u57CE\u56FD\u9645\u3002\u5F53\u5E74\u5728\u4E00\u9662\u9762\u8BD5\uFF0C\u4EBA\u529B\u8DDF\u6211\u8BF4\u4F60\u627E\u4E2A\u822A\u5929\u7684\u5973\u670B\u53CB\uFF0C\u4E24\u4E2A\u4EBA\u6512\u4E94\u5E74\u53EF\u4EE5\u4E0D\u9760\u5BB6\u91CC\u5728\u5317\u4EAC\u4E70\u623F\u3002\u6211\u4EEC\u8FD9\u8FB9\u9644\u8FD1\u5404\u65B9\u9762\u8FD8\u53EF\u4EE5\u7684\u623F\u5B50\uFF08100\u5E73\u5DE6\u53F3\uFF0C\u5357\u5317\u901A\u900F\uFF0C\u5C0F\u533A\u8FD8\u53EF\u4EE5\uFF09\uFF0C\u5DEE\u4E0D\u591A200w\uFF0C\u6211\u5468\u56F4\u7684\u5E74\u8F7B\u4EBA\u57FA\u672C\u5165\u804C\u4E24\u5E74\u4E5F\u90FD\u4E70\u623F\u4E86\uFF0C\u6BD5\u7ADF\u4E94\u516D\u5341\u4E07\u7684\u9996\u4ED8\u81EA\u5DF1\u6512\u4E00\u70B9\u5BB6\u91CC\u51D1\u4E00\u70B9\u8FD8\u662F\u5BB9\u6613\u5F88\u591A\u3002</p><p>\u6211\u89C9\u5F97\u5427\uFF0C\u8861\u91CF\u4E00\u4E2A\u5DE5\u4F5C\u597D\u574F\uFF0C\u4E0D\u540C\u7684\u4EBA\u6709\u4E0D\u540C\u7684\u8861\u91CF\u6807\u51C6\uFF0C\u6211\u672C\u6765\u60F3\u8BF4\u94B1\u80AF\u5B9A\u662F\u6700\u91CD\u8981\u7684\u4F46\u4E0D\u662F\u552F\u4E00\u7684\uFF0C\u540E\u6765\u89C9\u5F97\u53EF\u80FD\u5BF9\u6709\u4E9B\u4EBA\u6765\u8BF4\u94B1\u90FD\u4E0D\u662F\u6700\u91CD\u8981\u7684\u3002</p><p>\u4E0E\u6211\u800C\u8A00\uFF0C\u6536\u5165\uFF0C\u7A33\u5B9A\u6027\uFF0C\u662F\u4E0D\u662F\u559C\u6B22\uFF0C\u6709\u6CA1\u6709\u610F\u4E49\uFF0C\u6211\u80FD\u4E0D\u80FD\u80DC\u4EFB\uFF0C\u7D2F\u4E0D\u7D2F\uFF0C\u5730\u65B9\u5728\u54EA\u79BB\u5BB6\u8FD1\u4E0D\u8FD1\u2026\u90FD\u662F\u9700\u8981\u8003\u8651\u7684\u95EE\u9898\u3002</p><p>\u6211\u89C9\u5F97\u7814\u7A76\u6240\u771F\u6B63\u7684\u5438\u5F15\u529B\u5728\u4E8E\u8FD9\u4E48\u51E0\u70B9\uFF1A</p><p>1\u3001\u6700\u91CD\u8981\u7684\uFF0C\u4F60\u505A\u7684\u4E8B\u662F\u5BF9\u56FD\u5BB6\u5BF9\u6C11\u65CF\u5BF9\u793E\u4F1A\u5BF9\u81EA\u5DF1\u6709\u610F\u4E49\u7684\u4E8B\u3002</p><p>\u4F60\u4E0D\u53EA\u4E3A\u4E86\u8C0B\u751F\u800C\u5355\u7EAF\u7684\u5728\u7ED9\u8D44\u672C\u5BB6\u6253\u5DE5\uFF0C\u4E0D\u662F\u5728\u5E2E\u8D44\u672C\u5BB6\u5272\u97ED\u83DC\uFF0C\u66F4\u4E0D\u662F\u5728\u5E2E\u5916\u56FD\u7684\u8D44\u672C\u5272\u4E2D\u56FD\u4EBA\u7684\u97ED\u83DC\uFF0C\u4F60\u662F\u5728\u4E3A\u4E2D\u534E\u6C11\u65CF\u7684\u4F1F\u5927\u590D\u5174\u505A\u8D21\u732E\uFF0C\u8FD9\u91CC\u6015\u5F97\u7F6A\u4EBA\u88AB\u55B7\uFF0C\u6211\u4E0D\u5C55\u5F00\u8BF4\u4E86\u3002</p><p>\u6211\u6CA1\u6709\u5728\u5F00\u73A9\u7B11\uFF0C\u6211\u73B0\u5728\u7684\u5DE5\u4F5C\u662F\u7EFC\u5408\u8003\u8651\u5BB6\u5EAD\uFF08\u6211\u662F\u897F\u5B89\u571F\u8457\uFF09\uFF0C\u6536\u5165\uFF0C\u4E13\u4E1A\uFF0C\u7A33\u5B9A\u7B49\u5404\u4E2A\u65B9\u9762\u7684\u6700\u4F18\u89E3\u3002\u4F46\u662F\u6211\u53F8\u505A\u7684\u4E1C\u897F\u6CA1\u6709\u822A\u6BCD\uFF0C\u6838\u6F5C\u8247\uFF0C\u6B7C20\uFF0C\u8FD020\uFF0C\u8FDC\u8F70\uFF0C\u6218\u7565\u5BFC\u5F39\uFF0C\u53CD\u8230\u5F39\u9053\u5BFC\u5F39\uFF0C\u9AD8\u8D85\u58F0\u901F\u5DE1\u822A\u6B66\u5668\uFF0C\u8F7D\u4EBA\u822A\u5929\u90A3\u4E48\u725B\u903C\u95EA\u95EA\uFF0C\u6211\u6709\u65F6\u5019\u8DDF\u540C\u5B66\u5E08\u5144\u5F1F\u4EEC\u804A\u5929\u7684\u65F6\u5019\u60F3\u60F3\u4EBA\u5BB6\u505A\u7684\u4E1C\u897F\uFF0C\u662F\u6709\u4E9B\u9057\u61BE\u7684\uFF0C\u867D\u7136\u8FD9\u4E9B\u5355\u4F4D\u6027\u4EF7\u6BD4\u751A\u81F3\u7EDD\u5BF9\u6536\u5165\u90FD\u4E0D\u5982\u6211\u4EEC\u3002</p><p>2\u3001\u7A33\u5B9A\uFF0C\u6211\u5DE5\u4F5C\u4E86\u4E00\u4E2A\u6708\u5C31\u660E\u767D\u4E86\u4E00\u4E2A\u9053\u7406</p><p>\u5176\u5B9E\u5F88\u591A\u4F01\u4E1A\u80FD\u7ED9\u4EBA\u90A3\u4E48\u591A\u94B1\uFF0C\u662F\u56E0\u4E3A\u6CA1\u6709\u5F88\u5927\u7684\u8D1F\u62C5\uFF0C\u53EF\u4EE5\u7ED9\u62FC\u547D\u5E72\u6D3B\u7684\u5E74\u8F7B\u4EBA\u5F88\u591A\u94B1\uFF0C\u53EF\u662F\uFF0C\u8C01\u53C8\u80FD\u4E00\u76F4\u62FC\u547D\u4E0B\u53BB\u5462\uFF0C\u8C01\u53C8\u80FD\u4FDD\u8BC1\u81EA\u5DF1\u4E00\u5B9A\u53EF\u4EE5\u4E0A\u5F97\u53BB\u5462\uFF0C\u5347\u804C\u8FD9\u79CD\u4E8B\u4E0D\u5149\u8981\u81EA\u5DF1\u52AA\u529B\uFF0C\u4E5F\u8981\u673A\u9047\u7684\uFF0C\u6240\u4EE5\u5F88\u591A\u770B\u4F3C\u76EE\u524D\u6536\u5165\u5F88\u9AD8\u7684\u5DE5\u4F5C\uFF0C\u5176\u5B9E\u662F\u628A\u672A\u6765\u5F88\u591A\u5E74\u7684\u94B1\uFF0C\u62FF\u51FA\u4E00\u5C0F\u90E8\u5206\u73B0\u5728\u7ED9\u4F60\uFF0C\u8BA9\u4F60\u62FC\u547D\u5E72\u6D3B\u3002</p><p>\u6362\u8A00\u4E4B\uFF0C\u6211\u4EEC\u73B0\u5728\u7684\u6536\u5165\u4E00\u822C\uFF0C\u5176\u5B9E\u662F\u628A\u4E00\u90E8\u5206\u8BE5\u7ED9\u5E74\u8F7B\u4EBA\u62FC\u547D\u7684\u94B1\u7ED9\u4E86\u809D\u4E0D\u52A8\u7684\u540C\u5FD7\uFF0C\u6211\u89C9\u5F97\u8FD9\u4E5F\u6CA1\u5565\uFF0C\u6BD5\u7ADF\u73B0\u5728\u5355\u4F4D\u7684\u57FA\u4E1A\u4E5F\u662F\u4ED6\u4EEC\u809D\u51FA\u6765\u7684\u3002</p><p>\u6211\u4E00\u76F4\u89C9\u5F97\u4E00\u4E2A\u7406\u8BBA\u633A\u5BF9\u7684\uFF0C\u4ECE\u4F53\u5236\u5185\u51FA\u53BB\uFF0C\u6536\u5165\u7FFB\u7FFB\u662F\u81F3\u5C11\u7684\uFF0C\u8981\u4E0D\u7136\u60F3\u90FD\u522B\u60F3\u3002\u4E0D\u7528\u62C5\u5FC3\u516C\u53F8\u5012\u95ED\uFF0C\u4E0D\u7528\u62C5\u5FC3\u81EA\u5DF1\u88AB\u4F18\u5316\uFF0C\u4E5F\u5C31\u5C11\u4E86\u5F88\u591A\u52FE\u5FC3\u6597\u89D2\uFF0C\u5B89\u5FC3\u5E72\u81EA\u5DF1\u5DE5\u4F5C\u5C31\u597D\uFF0C\u8FD9\u79CD\u611F\u89C9\u4E5F\u662F\u53EF\u4EE5\u6298\u73B0\u7684\u3002</p><p>\u6240\u4EE5\u6211\u5F88\u540C\u610F\u522B\u7684\u7B54\u4E3B\u8BF4\u7684\u822A\u5929\u9002\u5408\u5BB6\u5883\u5C0F\u5EB7\u7684\u4EBA\uFF0C\u6BD5\u7ADF\u5728\u5E74\u8F7B\u4EBA\u5C24\u5176\u662F\u7537\u4EBA\u6700\u7F3A\u94B1\u768425-35\u5C81\uFF0C\u7CFB\u7EDF\u5185\u53EF\u80FD\u540C\u6837\u9700\u8981\u62FC\u547D\u4F46\u662F\u4E0D\u7ED9\u4F60\u90A3\u4E48\u591A\u94B1\u3002\u8FD9\u4E2A\u65F6\u5019\u5982\u679C\u5BB6\u91CC\u80FD\u7ED9\u4E00\u7B14\u542F\u52A8\u8D44\u91D1\u6216\u8005\u5168\u6B3E\u4E70\u623F\uFF0C\u751F\u6D3B\u5C31\u4E0D\u4F1A\u6349\u895F\u89C1\u8098\u3002\u5C31\u6211\u4E2A\u4EBA\u800C\u8A00\uFF0C\u6211\u5BB6\u5728\u897F\u5B89\u7B97\u5C0F\u5EB7\uFF0C\u5728\u5317\u4EAC\u662F\u8D64\u8D2B\u3002\u6240\u4EE5\u5728\u897F\u5B89\u7684\u7814\u7A76\u6240\u6211\u89C9\u5F97\u81EA\u5DF1\u6536\u5165\u8FD8\u53EF\u4EE5\u3002\u6211\u9762\u5B8C\u8F7D\u4EBA\u822A\u5929\u603B\u4F53\u90E8\u641C\u4E86\u4E00\u4E0B\u5468\u56F4\u5C0F1000\u4E07\u7684\u623F\u5B50\uFF0C\u60F3\u4E86\u60F3\u6708\u4F9B\u4E09\u4E07\u7684\u65E5\u5B50\uFF0C\u6211\u89C9\u5F97\u6211\u7684\u5BB6\u5EAD\u627F\u8F7D\u4E0D\u4E86\u8FD9\u6837\u7684\u7406\u60F3\u3002</p><hr><p>\u5206\u4EAB\u7ED3\u675F\uFF0C\u4F60\u7684\u9009\u62E9\u662F\u4E92\u8054\u7F51 or \u7814\u7A76\u6240\uFF1F</p><p><img src="http://cdn.tobebetterjavaer.com/tobebetterjavaer/images/manongshenghuo/yanjiusuo-20wan-3.png" alt="" loading="lazy"></p><p><em>\u6CA1\u6709\u4EC0\u4E48\u4F7F\u6211\u505C\u7559\u2014\u2014\u9664\u4E86\u76EE\u7684\uFF0C\u7EB5\u7136\u5CB8\u65C1\u6709\u73AB\u7470\u3001\u6709\u7EFF\u836B\u3001\u6709\u5B81\u9759\u7684\u6E2F\u6E7E\uFF0C\u6211\u662F\u4E0D\u7CFB\u4E4B\u821F</em>\u3002</p>',36);function g(w,f){const t=a("ExternalLinkIcon");return n(),r("div",null,[l,h,e("p",null,[_,e("a",d,[m,s(t)]),u]),b])}var y=o(i,[["render",g],["__file","yanjiusuo-20wan.html.vue"]]);export{y as default};
